const MENU_KEY_PREFIX = 'menu_';

export function saveMenu(menuData) {
  const menuId = generateId();
  const menuKey = MENU_KEY_PREFIX + menuId;
  localStorage.setItem(menuKey, JSON.stringify(menuData));
  return menuId;
}

export function getMenu(menuId) {
  const menuKey = MENU_KEY_PREFIX + menuId;
  const menuData = localStorage.getItem(menuKey);
  return menuData ? JSON.parse(menuData) : null;
}

export function getAllMenus() {
  const menus = [];
  for (let i = 0; i < localStorage.length; i++) {
    const key = localStorage.key(i);
    if (key.startsWith(MENU_KEY_PREFIX)) {
      const menuData = localStorage.getItem(key);
      menus.push(JSON.parse(menuData));
    }
  }
  return menus;
}

export function cleanOldMenus() {
  const now = new Date();
  for (let i = 0; i < localStorage.length; i++) {
    const key = localStorage.key(i);
    if (key.startsWith(MENU_KEY_PREFIX)) {
      const menuData = JSON.parse(localStorage.getItem(key));
      const createdDate = new Date(menuData.createdAt);
      const diff = now.getTime() - createdDate.getTime();
      const days = Math.ceil(diff / (1000 * 3600 * 24));
      if (days > 7) {
        localStorage.removeItem(key);
      }
    }
  }
}

function generateId() {
  return Math.random().toString(36).substring(2, 15) + Math.random().toString(36).substring(2, 15);
}
