// src/services/syncService.js

// Servicio para gestionar la sincronizaciÃ³n y el estado de la conexiÃ³n
import { 
  getPendingPlatos, 
  updatePlatoSyncStatus, 
  getSyncQueue, 
  removeFromSyncQueue,
  getPlato
} from './indexedDBService';
import { adaptPlatoData } from '../utils/dataAdapters';

// Constantes desde el servicio IndexedDB
const API_URL = 'http://localhost:3000/api';
const RAW_URL = 'http://localhost:3000/raw';

// ConfiguraciÃ³n especÃ­fica para mejorar la sincronizaciÃ³n
const SYNC_CONFIG = {
  skipServerAvailabilityCheck: true,
  maxPlatoSize: 30000, // 30KB mÃ¡ximo
  retryIntervals: [5000, 15000, 30000],
  maxRetries: 2,
  platoUrl: `${API_URL}/sync/platos`,
  emergencyPlatoUrl: `${API_URL}/platos/minimal`,
  useEmergencyEndpoint: true
};

// Estado de la sincronizaciÃ³n
let isSyncing = false;
let _isOnline = navigator.onLine;

// Alternativa mÃ¡s simple para verificar la conexiÃ³n
const isOnline = async () => {
  // Primero comprobamos el estado de navigator.onLine
  if (!navigator.onLine) {
    return false;
  }

  // Intentamos hacer un fetch a un recurso pequeÃ±o (como un favicon)
  try {
    const response = await fetch(`${API_URL}/favicon.ico`, {
      method: 'HEAD',
      mode: 'no-cors',
      cache: 'no-store',
      credentials: 'omit'
    });

    return true; // Si no hay excepciÃ³n, asumimos que estamos en lÃ­nea
  } catch (error) {
    console.error('Error al verificar estado de conexiÃ³n:', error);
    return false;
  }
};

// Configurar escuchas para eventos de conexiÃ³n
const setupConnectionListeners = () => {
  window.addEventListener('online', async () => {
    console.log('Evento online detectado');
    _isOnline = await isOnline();
    if (_isOnline) {
      console.log('ConexiÃ³n a Internet restablecida');
      // Sincronizar cuando se recupera la conexiÃ³n
      syncData();
    }
  });

  window.addEventListener('offline', () => {
    console.log('Evento offline detectado');
    _isOnline = false;
    console.log('ConexiÃ³n a Internet perdida');
  });

  // Verificar estado inicial
  isOnline().then(online => {
    console.log(`Estado inicial de conexiÃ³n: ${online ? 'En lÃ­nea' : 'Fuera de lÃ­nea'}`);
  });
};

// FunciÃ³n para probar la conexiÃ³n con el servidor (modo ultra-ligero)
const testServerConnection = async () => {
  console.log('ðŸ”„ Probando conexiÃ³n con el servidor (modo emergencia)...');

  try {
    // Usar XMLHttpRequest sin cookies ni headers personalizados
    const xhr = new XMLHttpRequest();

    const responsePromise = new Promise((resolve) => {
      const timeoutId = setTimeout(() => {
        console.warn('â±ï¸ Timeout en la prueba de conexiÃ³n');
        xhr.abort();
        resolve(false);
      }, 5000);

      xhr.onreadystatechange = function() {
        if (xhr.readyState === 4) {
          clearTimeout(timeoutId);
          if (xhr.status >= 200 && xhr.status < 300) {
            console.log('âœ… ConexiÃ³n bÃ¡sica establecida');
            resolve(true);
          } else if (xhr.status === 431) {
            console.error('âŒ Error 431: Request Header Fields Too Large');
            console.log('ðŸ‘‰ Necesitas reducir el tamaÃ±o de las cookies o headers');
            resolve(false);
          } else {
            console.error(`âŒ Error en prueba de conexiÃ³n: ${xhr.status}`);
            resolve(false);
          }
        }
      };

      xhr.onerror = function() {
        clearTimeout(timeoutId);
        console.error('âŒ Error de red en prueba de conexiÃ³n');
        // Intentar con endpoint de emergencia sin headers
        checkAlternativePort().then(resolve);
      };

      // Realizar una solicitud GET simple sin headers
      xhr.open('GET', `${API_URL}/test/ping`, true);
      // No aÃ±adir ningÃºn header
      xhr.withCredentials = false; // Importante: evitar enviar cookies
      xhr.send();
    });

    return await responsePromise;
  } catch (error) {
    console.error('âŒ Error en prueba de conexiÃ³n:', error);
    return false;
  }
};

// Comprobar puerto alternativo
const checkAlternativePort = async () => {
  console.log('ðŸ”„ Probando puertos alternativos...');

  // Lista de puertos comunes para probar
  const ports = [8080, 3000, 5000, 8000];

  for (const port of ports) {
    try {
      console.log(`Probando en puerto: ${port}`);
      const response = await fetch(`http://localhost:${port}/raw/ping`, {
        method: 'GET',
        mode: 'no-cors',
        cache: 'no-store',
        credentials: 'omit'
      });

      if (response.ok || response.status === 0) {
        console.log(`âœ… Servidor encontrado en puerto ${port}`);
        // Actualizar URL global para usar este puerto
        window.SERVER_PORT = port;
        return true;
      }
    } catch (error) {
      console.log(`Puerto ${port} no disponible`);
    }
  }

  console.error('âŒ No se encontrÃ³ el servidor en ningÃºn puerto comÃºn');
  return false;
};

// FunciÃ³n para probar la conexiÃ³n a MySQL
const testMySQLConnection = async () => {
  console.log('ðŸ”„ Probando conexiÃ³n a MySQL...');

  try {
    const response = await fetch(`${API_URL}/test/db`, {
      method: 'GET',
      headers: {
        'Accept': 'application/json'
      }
    });

    if (response.ok) {
      const data = await response.json();
      console.log('âœ… ConexiÃ³n a MySQL establecida:', data);

      // Mostrar informaciÃ³n clara sobre la base de datos
      if (data.data && data.data.database) {
        console.log(`ðŸ“Š Base de datos MySQL: ${data.data.database}`);
        console.log(`ðŸ“Š Tablas disponibles: ${data.data.tables.join(', ')}`);
      }

      return true;
    } else {
      console.error('âŒ Error al conectar con MySQL:', await response.text());
      return false;
    }
  } catch (error) {
    console.error('âŒ Error de red al probar la conexiÃ³n MySQL:', error);
    return false;
  }
};

// FunciÃ³n para sincronizar datos entre IndexedDB y el servidor
const syncData = async () => {
  if (!await isOnline()) {
    console.log('Sin conexiÃ³n a Internet, la sincronizaciÃ³n no es posible');
    return false;
  }

  if (isSyncing) {
    console.log('Ya hay una sincronizaciÃ³n en progreso');
    return false;
  }

  isSyncing = true;

  console.log('ðŸ”„ Iniciando sincronizaciÃ³n de datos...');

  try {
    // Procesar la cola de sincronizaciÃ³n
    const syncQueue = await getSyncQueue();
    console.log(`ðŸ“‹ Cola de sincronizaciÃ³n: ${syncQueue.length} elementos`);

    for (const item of syncQueue) {
      if (item.entityType === 'plato') {
        // Obtener el plato completo de IndexedDB
        const plato = await getPlato(item.entityId);

        if (plato) {
          console.log(`ðŸ½ï¸ Sincronizando plato: ${plato.name} (${plato.id})`);

          try {
            // Intentar sincronizaciÃ³n minimalista
            const success = await syncPlato(plato);

            if (success.success) {
              console.log(`âœ… Plato ${plato.id} sincronizado correctamente`);
              await removeFromSyncQueue(item.id);
            } else {
              console.error(`âŒ Error al sincronizar plato ${plato.id}`);
            }
          } catch (syncError) {
            console.error(`âŒ ExcepciÃ³n al sincronizar plato ${plato.id}:`, syncError);
          }
        } else {
          console.warn(`âš ï¸ Plato ${item.entityId} no encontrado en IndexedDB`);
          await removeFromSyncQueue(item.id);
        }
      }
    }

    // Buscar platos pendientes que no estÃ©n en la cola
    console.log('ðŸ” Buscando platos pendientes de sincronizaciÃ³n...');
    const pendingPlatos = await getPendingPlatos();
    console.log(`ðŸ“‹ Platos pendientes: ${pendingPlatos.length}`);

    for (const plato of pendingPlatos) {
      try {
        console.log(`ðŸ½ï¸ Sincronizando plato pendiente: ${plato.name} (${plato.id})`);
        const success = await syncPlato(plato);

        if (success.success) {
          console.log(`âœ… Plato pendiente ${plato.id} sincronizado correctamente`);
        } else {
          console.error(`âŒ Error al sincronizar plato pendiente ${plato.id}: ${success.error}`);
        }
      } catch (syncError) {
        console.error(`âŒ ExcepciÃ³n al sincronizar plato pendiente ${plato.id}: ${syncError}`);
      }
    }

    console.log('âœ… SincronizaciÃ³n completada');
    return true;
  } catch (error) {
    console.error('âŒ Error durante la sincronizaciÃ³n:', error);
    return false;
  } finally {
    isSyncing = false;
  }
};

// MÃ©todo ultra-minimal usando XMLHttpRequest sin headers
const tryUltraMinimalSync = async (data, platoId) => {
  try {
    console.log('ðŸš¨ Intentando sincronizaciÃ³n ultra-minimal para plato:', platoId);

    // Crear un objeto XMLHttpRequest directamente
    const xhr = new XMLHttpRequest();

    // Configurar una promesa para manejar la respuesta
    const responsePromise = new Promise((resolve) => {
      // Configurar timeout
      const timeoutId = setTimeout(() => {
        console.warn('â±ï¸ Timeout en sincronizaciÃ³n ultra-minimal');
        xhr.abort();
        resolve(false);
      }, 15000);

      xhr.onreadystatechange = async function() {
        if (xhr.readyState === 4) {
          clearTimeout(timeoutId);
          if (xhr.status >= 200 && xhr.status < 300) {
            console.log('âœ… SincronizaciÃ³n ultra-minimal exitosa');
            try {
              await updatePlatoSyncStatus(platoId, 'synced');
            } catch (dbError) {
              console.error('Error al actualizar estado en BD:', dbError);
            }
            resolve(true);
          } else {
            console.error(`âŒ Error en sincronizaciÃ³n ultra-minimal: ${xhr.status}`);
            resolve(false);
          }
        }
      };

      xhr.onerror = function() {
        clearTimeout(timeoutId);
        console.error('âŒ Error de red en sincronizaciÃ³n ultra-minimal');
        resolve(false);
      };
    });

    // Usar mÃ©todo POST con datos minimalistas
    xhr.open('POST', `${API_URL}/platos/emergency-sync`, true);

    // Solo agregar Content-Type, nada mÃ¡s
    xhr.setRequestHeader('Content-Type', 'application/json');

    // Preparar datos ultra-minimalistas
    const adaptedData = data; // Deshabilitado adaptPlatoData temporalmente
    const minimalData = {
      id: adaptedData.id,
      name: (adaptedData.name || "").substring(0, 30),
      price: Number(adaptedData.price) || 0
    };

    // Enviar los datos
    xhr.send(JSON.stringify(minimalData));

    return await responsePromise;
  } catch (error) {
    console.error('âŒ Error en sincronizaciÃ³n ultra-minimal:', error);
    return false;
  }
};

// Procesar la cola de sincronizaciÃ³n
const processSyncQueue = async () => {
  if (!await isOnline() || isSyncing) {
    console.log(`No se puede procesar la cola. En lÃ­nea: ${_isOnline}, Sincronizando: ${isSyncing}`);
    return false;
  }

  isSyncing = true;
  console.log('Iniciando procesamiento de cola de sincronizaciÃ³n...');

  try {
    const syncItems = await getSyncQueue();

    if (syncItems.length === 0) {
      console.log('No hay elementos en la cola de sincronizaciÃ³n');
      isSyncing = false;
      return true;
    }

    console.log(`Procesando ${syncItems.length} elementos en la cola de sincronizaciÃ³n`);

    // Procesar cada elemento de la cola
    for (const item of syncItems) {
      let syncSuccess = false;

      if (item.type === 'create' && item.entityType === 'plato') {
        syncSuccess = await syncPlatoToServer(item.data);
      }
      // AquÃ­ pueden agregarse mÃ¡s tipos de sincronizaciÃ³n segÃºn sea necesario

      if (syncSuccess) {
        // Eliminar de la cola si la sincronizaciÃ³n fue exitosa
        await removeFromSyncQueue(item.id);
        console.log(`Elemento ${item.id} eliminado de la cola de sincronizaciÃ³n`);
      } else {
        console.log(`No se pudo sincronizar el elemento ${item.id}, se reintentarÃ¡ mÃ¡s tarde`);
      }
    }

    console.log('Procesamiento de cola de sincronizaciÃ³n completado');
    return true;
  } catch (error) {
    console.error('Error al procesar cola de sincronizaciÃ³n:', error);
    return false;
  } finally {
    isSyncing = false;
  }
};

// MÃ©todo de sincronizaciÃ³n extremadamente simplificado para problemas persistentes
const syncMinimalDataOnly = async (data, platoId, retryCount = 0) => {
  const maxRetries = SYNC_CONFIG.maxRetries;

  // Usar datos minimalistas pero incluir la imagen
  const adaptedData = data; // Deshabilitado adaptPlatoData temporalmente
  const minimalData = {
    id: adaptedData.id,
    name: adaptedData.name?.substring(0, 50) || 'Plato sin nombre',
    price: Number(adaptedData.price) || 0,
    description: adaptedData.description?.substring(0, 100) || '',
    availableQuantity: Number(adaptedData.availableQuantity) || 0,
    is_available: Boolean(adaptedData.is_available),
    image: adaptedData.image || null, // Incluir la imagen
    minimal_sync: true
  };

  return new Promise((resolve) => {
    // Establecer timeout para toda la operaciÃ³n
    const operationTimeoutId = setTimeout(() => {
      console.warn('â±ï¸ Timeout global de la operaciÃ³n de sincronizaciÃ³n');
      resolve(false);
    }, 30000);

    const xhr = new XMLHttpRequest();

    // Configurar timeouts mÃ¡s estrictos
    xhr.timeout = 15000;

    xhr.onreadystatechange = async function() {
      if (xhr.readyState === 4) {
        if (xhr.status >= 200 && xhr.status < 300) {
          clearTimeout(operationTimeoutId);
          try {
            await updatePlatoSyncStatus(platoId, 'synced');
            console.log(`âœ… SincronizaciÃ³n exitosa para plato ${platoId}`);
          } catch (error) {
            console.error('Error al actualizar estado:', error);
          }
          resolve(true);
        } else if (xhr.status === 0) {
          // ConexiÃ³n abortada o no establecida
          console.warn('âš ï¸ ConexiÃ³n interrumpida');
          handleRetry();
        } else if (xhr.status === 431) {
          console.error(`âŒ Error 431 (Headers demasiado grandes) - Intentando mÃ©todo ultraminimal`);
          tryUltraMinimalSync(adaptedData, platoId).then(success => {
            clearTimeout(operationTimeoutId);
            resolve(success);
          });
        } else {
          console.error(`âŒ Error HTTP ${xhr.status} en sincronizaciÃ³n`);
          handleRetry();
        }
      }
    };

    xhr.ontimeout = function() {
      console.warn('â±ï¸ Timeout de la peticiÃ³n HTTP');
      handleRetry();
    };

    xhr.onerror = function() {
      console.error('âŒ Error de red en sincronizaciÃ³n');
      handleRetry();
    };

    // FunciÃ³n para manejar reintentos
    const handleRetry = () => {
      if (retryCount < maxRetries) {
        const delay = SYNC_CONFIG.retryIntervals[retryCount] || 5000;
        console.log(`ðŸ”„ Reintentando en ${delay}ms... (${retryCount + 1}/${maxRetries})`);

        setTimeout(() => {
          clearTimeout(operationTimeoutId);
          syncMinimalDataOnly(data, platoId, retryCount + 1)
            .then(success => resolve(success));
        }, delay);
      } else {
        clearTimeout(operationTimeoutId);
        console.warn('âŒ Agotados todos los reintentos de sincronizaciÃ³n');
        resolve(false);
      }
    };

    try {
      // Usar mÃ©todo POST con headers mÃ­nimos
      xhr.open('POST', `${API_URL}/platos`, true);
      xhr.setRequestHeader('Content-Type', 'application/json');

      // Enviar los datos minimalistas
      xhr.send(JSON.stringify(minimalData));
    } catch (error) {
      console.error('âŒ Error al iniciar la peticiÃ³n:', error);
      handleRetry();
    }
  });
};

// Sincronizar un plato con el servidor
const syncPlatoToServer = async (plato, retryCount = 0) => {
  const maxRetries = SYNC_CONFIG.maxRetries;

  // Si el plato estÃ¡ marcado como problemÃ¡tico, omitirlo
  if (plato.sync_problematic) {
    console.warn(`âš ï¸ Omitiendo plato ${plato.id} marcado como problemÃ¡tico para sincronizaciÃ³n`);
    return false;
  }

  console.log(`Sincronizando plato ${plato.id} con el servidor (intento ${retryCount + 1}/${maxRetries + 1})...`);

  try {
    // Omitir verificaciÃ³n si estÃ¡ desactivada
    if (!SYNC_CONFIG.skipServerAvailabilityCheck) {
      // Verificar si el servidor estÃ¡ disponible
      const serverAvailable = await checkServerAvailability();
      if (!serverAvailable) {
        console.error('âŒ El servidor no estÃ¡ disponible. Posponiendo sincronizaciÃ³n.');
        return false;
      }
    }

    // Preparar datos para sincronizaciÃ³n incluyendo la imagen
    let adaptedData = plato; // Deshabilitado adaptPlatoData temporalmente
    let syncData = {
      id: adaptedData.id,
      name: adaptedData.name?.substring(0, 50) || 'Plato sin nombre',
      price: Number(adaptedData.price) || 0,
      description: adaptedData.description?.substring(0, 100) || '',
      availableQuantity: Number(adaptedData.availableQuantity) || 0,
      is_available: Boolean(adaptedData.is_available),
      image: adaptedData.image || null, // Incluir la imagen
      minimal_sync: true
    };

    // Usar el mÃ©todo mÃ¡s confiable para sincronizaciÃ³n
    return await syncPlato(adaptedData);
  } catch (error) {
    console.error('âŒ Error en sincronizaciÃ³n:', error);

    if (retryCount >= maxRetries) {
      try {
        await markPlatoAsSyncProblematic(plato.id);
      } catch (markError) {
        console.error('Error al marcar plato como problemÃ¡tico:', markError);
      }
    }

    return false;
  }
};

// Verificar estado de sincronizaciÃ³n con el backend
const checkSyncStatus = async () => {
  try {
    console.log('ðŸ”„ Verificando estado de sincronizaciÃ³n...');

    const response = await fetch(`${API_URL}/sync/status`, {
      method: 'GET',
      headers: { 'Accept': 'application/json' },
      credentials: 'omit' // Importante: no enviar cookies
    });

    if (response.ok) {
      const data = await response.json();
      console.log('ðŸ“Š Estado de sincronizaciÃ³n:', data);

      // Si no existe la tabla, intentar crearla
      if (!data.data.mysql.tablaPlatos) {
        console.log('âš ï¸ La tabla platos no existe, intentando configurar...');
        await setupSync();
      }

      return data.data;
    } else {
      console.error('âŒ Error al verificar estado de sincronizaciÃ³n:', await response.text());
      return null;
    }
  } catch (error) {
    console.error('âŒ Error al verificar estado de sincronizaciÃ³n:', error);
    return null;
  }
};

// Configurar la sincronizaciÃ³n - crear tablas necesarias
const setupSync = async () => {
  try {
    console.log('ðŸ”„ Configurando sincronizaciÃ³n...');

    const response = await fetch(`${API_URL}/sync/setup`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      credentials: 'omit',
      body: JSON.stringify({ force: false })
    });

    if (response.ok) {
      const data = await response.json();
      console.log('âœ… ConfiguraciÃ³n de sincronizaciÃ³n completada:', data);
      return true;
    } else {
      console.error('âŒ Error al configurar sincronizaciÃ³n:', await response.text());
      return false;
    }
  } catch (error) {
    console.error('âŒ Error al configurar sincronizaciÃ³n:', error);
    return false;
  }
};

// Inicializar el servicio de sincronizaciÃ³n
const initSyncService = () => {
  console.log('ðŸ”„ Inicializando servicio de sincronizaciÃ³n...');

  // Probar conexiones y configurar sincronizaciÃ³n
  Promise.all([
    testServerConnection(),
    checkSyncStatus()
  ]).then(([isApiConnected, syncStatus]) => {
    console.log(`âœ… Estado de conexiÃ³n directa - API: ${isApiConnected}`);

    if (isApiConnected) {
      // Configurar escuchas para eventos de conexiÃ³n
      setupConnectionListeners();

      // Si hay estado de sincronizaciÃ³n, proceder con la sincronizaciÃ³n
      if (syncStatus && syncStatus.mysql && syncStatus.mysql.connected) {
        console.log('ðŸ“Š MySQL conectado, procediendo con sincronizaciÃ³n');
        syncData();

        // Reactivar la sincronizaciÃ³n periÃ³dica (cada 1 minuto)
        const syncInterval = setInterval(syncData, 60000);
      } else {
        console.log('âš ï¸ MySQL no estÃ¡ configurado correctamente, intentando configurar...');
        setupSync().then(success => {
          if (success) {
            console.log('âœ… ConfiguraciÃ³n exitosa, iniciando sincronizaciÃ³n');
            syncData();

            // Reactivar la sincronizaciÃ³n periÃ³dica (cada 1 minuto)
            const syncInterval = setInterval(syncData, 60000);
          }
        });
      }
    } else {
      console.error('âŒ No se pudo establecer conexiÃ³n con el servidor.');
    }
  });

  console.log('âš™ï¸ Servicio de sincronizaciÃ³n inicializado');
};

// Exportar las funciones necesarias
export {
  initSyncService,
  syncData,
  isOnline,
  _isOnline,
  testServerConnection,
  testMySQLConnection,
  checkSyncStatus,
  setupSync
};

// AsegÃºrate de que la funciÃ³n que maneja la sincronizaciÃ³n tenga un mejor manejo de errores

const syncPlato = async (plato) => {
  console.log(`ðŸ”„ Sincronizando plato: ${plato.name} (ID: ${plato.id})`);

  try {
    // Preparar los datos para enviar (incluir la imagen)
    const platoToSync = {
      id: plato.id, // Incluir el ID del plato
      name: plato.name,
      description: plato.description || '',
      price: parseFloat(plato.price) || 0,
      category: plato.category || 'principal',
      image: plato.image || null, // Incluir la imagen en base64
      image_url: plato.image_url || '',
      is_available: plato.is_available !== false,
      includesDrink: plato.includesDrink || false,
      availableQuantity: plato.availableQuantity || 0
    };

    console.log('ðŸ“¤ Datos a enviar:', { ...platoToSync, image: platoToSync.image ? 'Imagen en base64 (omitida en log)' : null });

    // Imprimir la URL exacta para depuraciÃ³n
    const url = SYNC_CONFIG.platoUrl; // Usar la configuraciÃ³n existente
