    const isLoading = ref(true);
    const error = ref(null);
    const cartItems = ref([]);
    const orderRef = ref(null);
    console.log("SharedMenuView montado, ID del menú:", route.params.id);

    // Sistema de notificaciones toast
    const toast = ref({
      visible: false,
      message: '',
