    // Cargar el menú desde la API
    async function loadMenu() {
      try {
        isLoading.value = true;
        console.log("Cargando menú con ID:", route.params.id);
        if (!route.params.id) {
          error.value = 'ID de menú no especificado';
          isLoading.value = false;
          return;
