    const hasPaymentInfo = computed(() => {
      return menuData.value?.businessInfo?.paymentInfo && (menuData.value.businessInfo.paymentInfo.qrImage || menuData.value.businessInfo.paymentInfo.nequiNumber || menuData.value.businessInfo.paymentInfo.nequiImage);
    });

    // Contador total de items
    const totalItems = computed(() => {
      return cartItems.value.reduce((total, item) => total + item.quantity, 0);
    });

    // Total del carrito
    const cartTotal = computed(() => {
      return cartItems.value.reduce((total, item) => {
        return total + item.price * item.quantity;
      }, 0);
    });

    // Formatear precio
    function formatPrice(price) {
      if (isNaN(Number(price))) return '0,00';

      // Convertir a número y formatear con 2 decimales fijos
      const numero = Number(price).toFixed(2);

      // Separar parte entera y decimal
      const [parteEntera, parteDecimal] = numero.split('.');

      // Formatear parte entera con puntos como separadores de miles
      const parteEnteraFormateada = parteEntera.replace(/\B(?=(\d{3})+(?!\d))/g, '.');

      // Unir con coma como separador decimal
      return `${parteEnteraFormateada},${parteDecimal}`;
    }

    // Agregar al carrito con verificación de disponibilidad
    async function addToCart(item) {
      // Primero verificar disponibilidad
      const isAvailable = await checkAvailability(item);
      if (!isAvailable) return;
      const existingItem = cartItems.value.find(i => {
        // Asegurar que ambos items tienen ID y compararlos, o comparar por nombre si no hay ID
        if (item.id && i.id) {
          return i.id === item.id;
        }
        return i.name === item.name;
      });
      if (existingItem) {
        existingItem.quantity++;
      } else {
        cartItems.value.push({
          id: item.id || Date.now().toString(),
          name: item.name,
          price: item.price,
          quantity: 1,
          availableQuantity: item.availableQuantity || 10 // Temporal: 10 por defecto
        });
      }

      // Guardar carrito en localStorage
      saveCartToLocalStorage();

      // Mostrar notificación de éxito
      showToast(`${item.name} añadido al carrito`, 'success');
    }

    // Mostrar mensaje de producto añadido al carrito
    function showAddedToCart(productName) {
      const notification = document.createElement('div');
      notification.className = 'add-to-cart-notification';
      notification.innerText = `${productName} añadido al carrito`;
      document.body.appendChild(notification);
      setTimeout(() => {
        notification.classList.add('show');
        setTimeout(() => {
          notification.classList.remove('show');
          setTimeout(() => {
            document.body.removeChild(notification);
          }, 500);
        }, 2000);
      }, 100);
    }

    // Mostrar notificaciones toast
    function showToast(message, type = 'success', duration = 3000) {
      // Si ya hay un toast visible, limpiamos su timeout
      if (toast.value.timeoutId) {
        clearTimeout(toast.value.timeoutId);
      }

      // Configurar el nuevo toast
      toast.value.message = message;
      toast.value.type = type;
      toast.value.visible = true;
      console.log(`[Toast] ${type}: ${message}`);

      // Configurar timeout para ocultar después de la duración
      toast.value.timeoutId = setTimeout(() => {
        toast.value.visible = false;
      }, duration);
    }

    // Guardar carrito en localStorage
    function saveCartToLocalStorage() {
      try {
        localStorage.setItem(`cart_${route.params.id}`, JSON.stringify(cartItems.value));
      } catch (e) {
        console.error("Error al guardar carrito en localStorage:", e);
      }
    }

    // Incrementar cantidad
    function increaseQuantity(index) {
      cartItems.value[index].quantity++;
      saveCartToLocalStorage();
    }

    // Decrementar cantidad
    function decreaseQuantity(index) {
      if (cartItems.value[index].quantity > 1) {
        cartItems.value[index].quantity--;
        saveCartToLocalStorage();
      } else {
        removeFromCart(index);
      }
    }

    // Eliminar del carrito
    function removeFromCart(index) {
      cartItems.value.splice(index, 1);
      saveCartToLocalStorage();
    }

    // Completar pedido con actualización de inventario
    async function completeOrder() {
      // Validación de datos mínimos
      if (!customerInfo.value.name || !customerInfo.value.phone) {
        showToast('Por favor ingresa al menos tu nombre y teléfono para completar el pedido', 'error');
        return;
      }

      // Variable para indicar que el pedido está en proceso
      const isProcessingOrder = ref(true);
      try {
        // Actualizar inventario
        const inventoryUpdated = await updateInventory();
        if (!inventoryUpdated) {
          showToast('Error al actualizar el inventario. Por favor intenta nuevamente.', 'error');
          isProcessingOrder.value = false;
          return;
        }

        // Construir mensaje de confirmación incluyendo datos del cliente
        let confirmationMessage = `Pedido completado por un total de $${formatPrice(cartTotal.value)}\n`;
        confirmationMessage += `Cliente: ${customerInfo.value.name}\n`;
        confirmationMessage += `Teléfono: ${customerInfo.value.phone}\n`;
        confirmationMessage += `Forma de pago: ${selectedPaymentMethod.value === 'qr' ? 'Código QR' : selectedPaymentMethod.value === 'nequi' ? 'Nequi' : 'Contra entrega'}\n`;
        confirmationMessage += `\nGracias por tu compra!`;

        // Mostrar notificación de éxito
        showToast('¡Pedido completado con éxito!', 'success');

        // Limpiar carrito y datos del cliente
        cartItems.value = [];
        saveCartToLocalStorage();

        // Opcional: limpiar también los datos del cliente después de completar
        customerInfo.value = {
          name: '',
          phone: '',
          email: '',
          address: ''
        };
        additionalMessage.value = '';
      } catch (error) {
        console.error('Error al procesar el pedido:', error);
        showToast('Error al procesar el pedido. Por favor intenta nuevamente.', 'error');
      } finally {
        isProcessingOrder.value = false;
      }
    }

    // Desplazarse a la sección del pedido
    function scrollToOrder() {
      const orderSection = document.querySelector('.order-section');
      if (orderSection) {
        orderSection.scrollIntoView({
          behavior: 'smooth'
        });
      }
    }

