          const symbols = ['✦', '✧', '♣', '♠', '♥', '♫'];
          const symbol = symbols[index % symbols.length];
          message += `${symbol} ${item.name} x${item.quantity} - $${formatPrice(item.price * item.quantity)}\n`;
        });

        // Agregar total con formato destacado
        message += `\n*➤ TOTAL: $${formatPrice(cartTotal.value)}*\n`;

        // Agregar método de pago
        const paymentMethodText = selectedPaymentMethod.value === 'qr' ? 'Código QR' : selectedPaymentMethod.value === 'nequi' ? 'Nequi' : 'Contra entrega';
        message += `\n*➤ FORMA DE PAGO:* ${paymentMethodText}\n`;

        // Incluir mensaje adicional si existe
