          // Si hay un número, enviar directamente a ese número
          const formattedPhone = phoneNumber.replace(/\D/g, ''); // Quitar caracteres no numéricos
          whatsappUrl = `https://wa.me/${formattedPhone}?text=${encodeURIComponent(message)}`;
        } else {
          // Si no hay número, usar la opción de compartir
          whatsappUrl = `https://api.whatsapp.com/send?text=${encodeURIComponent(message)}`;
        }

        // Abrir WhatsApp
        window.open(whatsappUrl, '_blank');
