    // Verificar disponibilidad de inventario
    async function checkAvailability(item) {
      // Obtener la cantidad disponible actual (simulada para esta implementación)
      const availableQuantity = item.availableQuantity || 10; // Temporal: 10 por defecto

      // Obtener la cantidad actual en el carrito
      const cartItem = cartItems.value.find(i => i.id === item.id);
      const cartQuantity = cartItem ? cartItem.quantity : 0;

      // Verificar la disponibilidad
      if (availableQuantity <= 0) {
