import axios from 'axios';
import authHeader from './auth-header';

const API_URL = process.env.VUE_APP_API_URL || 'http://localhost:3000/api';

class RolService {
  getRoles() {
    return axios.get(`${API_URL}/roles`, { headers: authHeader() });
  }

  getRol(id) {
    return axios.get(`${API_URL}/roles/${id}`, { headers: authHeader() });
  }

  createRol(rol) {
    return axios.post(`${API_URL}/roles`, rol, { headers: authHeader() });
  }

  updateRol(id, rol) {
    return axios.put(`${API_URL}/roles/${id}`, rol, { headers: authHeader() });
  }

  deleteRol(id) {
    return axios.delete(`${API_URL}/roles/${id}`, { headers: authHeader() });
  }
}

export default new RolService();
