import { createRouter, createWebHistory } from 'vue-router'
import SharedMenuView from '../views/SharedMenuView.vue'
import HomePage from '../views/HomePage.vue'

const routes = [
  {
    path: '/',
    name: 'home',
    component: HomePage
  },
  {
    path: '/menu/:id',
    name: 'shared-menu',
    component: SharedMenuView,
    props: true
  }
  // Removemos las rutas que causan problemas (about, login, register)
]

const router = createRouter({
  history: createWebHistory(process.env.BASE_URL),
  routes
})

export default router
