import Vue from 'vue'

// Store simplificado sin Vuex
const store = {
  state: Vue.observable({
    // Aquí van tus estados existentes
    currentPublicMenuId: null // Almacena el ID del menú público
  }),
  
  mutations: {
    // Métodos para modificar el estado
    setCurrentPublicMenuId(id) {
      this.state.currentPublicMenuId = id;
    }
  },
  
  getters: {
    // Métodos para obtener el estado
    getCurrentPublicMenuId() {
      return this.state.currentPublicMenuId;
    }
  }
};

export default store
