          const soldItem = soldItems.value.find(sold => sold && typeof sold === 'object' && sold.name === itemName);
          if (soldItem) {
            // Si existe, aumentar la cantidad
            soldItem.quantity += 1;
            // Actualizar fecha
            if (!soldItem.date || new Date(currentDate) > new Date(soldItem.date)) {
              soldItem.date = currentDate;
            }
          } else {
            // Crear un objeto limpio y serializable para el item vendido
            const newSoldItem = {
              name: itemName,
              quantity: 1,
              price: Number(item.price) || 0,
              includesDrink: Boolean(item.includesDrink),
              date: currentDate
            };
            soldItems.value.push(newSoldItem);
          }

          // Para garantizar que todos los elementos son serializables
          const cleanSoldItems = soldItems.value.map(item => {
            // Verificar primero que item es un objeto
            if (!item || typeof item !== 'object') {
              return {
                name: 'Producto desconocido',
                quantity: 1,
                price: 0,
                includesDrink: false,
                date: currentDate
              };
            }
            return {
              name: String(item.name || 'Producto desconocido'),
              quantity: Number(item.quantity) || 1,
              price: Number(item.price) || 0,
              includesDrink: Boolean(item.includesDrink),
              date: item.date || currentDate
            };
          });

          // Guardar vendidos en IndexedDB
          await saveSoldItems(cleanSoldItems);

          // Eliminar del menú en IndexedDB - USAR LA FUNCIÓN RENOMBRADA
          if (item.id) {
            try {
              console.log(`Llamando a deleteMenuItemFromDB para ID: ${item.id}`);
              const deleteResult = await deleteMenuItemFromDB(item.id);
              console.log(`Resultado de la eliminación: ${deleteResult}`);
            } catch (deleteError) {
              console.error('Error al eliminar item del almacén:', deleteError);
              // Continuamos con el proceso aunque falle la eliminación en IndexedDB
            }
          }

          // Eliminar del array local
          specialMenuItems.value.splice(index, 1);

          // Actualizar también la versión local de soldItems
          soldItems.value = cleanSoldItems;
          console.log('Elemento eliminado correctamente del menú local');
        } catch (error) {
          console.error('Error al eliminar el plato:', error);
          await alert('Error al eliminar el plato. Por favor, intenta nuevamente.'); // Reemplazado: alert nativo por servicio personalizado
        }
      }
    };
    const sellSpecialMenuItem = async index => {
      try {
        const item = specialMenuItems.value[index];

        // Verificar si hay unidades disponibles
        if (item.availableQuantity <= 0) {
          await alert('No hay unidades disponibles de este plato.'); // Reemplazado: alert nativo por servicio personalizado
          return;
        }

        // Reducir la cantidad disponible
        item.availableQuantity--;

        // Actualizar lista de platos vendidos
        const soldItem = soldItems.value.find(sold => sold.name === item.name);
        const currentDate = new Date().toISOString();
        if (soldItem) {
          soldItem.quantity += 1;
          // Actualizar la fecha solo si es más reciente
          if (!soldItem.date || new Date(currentDate) > new Date(soldItem.date)) {
            soldItem.date = currentDate;
          }
        } else {
          // Crear un objeto limpio y serializable para el item vendido
          const newSoldItem = {
            name: item.name || 'Producto sin nombre',
            quantity: 1,
            price: Number(item.price) || 0,
            includesDrink: Boolean(item.includesDrink),
            date: currentDate
          };
          soldItems.value.push(newSoldItem);
        }

        // Para garantizar que todos los elementos son serializables
        const cleanSoldItems = soldItems.value.map(item => ({
          name: String(item.name || 'Producto desconocido'),
          quantity: Number(item.quantity) || 1,
          price: Number(item.price) || 0,
          includesDrink: Boolean(item.includesDrink),
          date: item.date || currentDate // Asegurar que todos los items tienen fecha
        }));

        // Guardar cambios en IndexedDB
        await saveMenuItems(specialMenuItems.value);
        await saveSoldItems(cleanSoldItems);

        // Actualizar también la versión local de soldItems
        soldItems.value = cleanSoldItems;

        // Mostrar confirmación
        await alert(`Se ha vendido una unidad de "${item.name}"`); // Añadido: mensaje de confirmación de venta
      } catch (error) {
        console.error('Error al vender el plato:', error);
        await alert('Error al procesar la venta. Por favor, intenta nuevamente.'); // Reemplazado: alert nativo por servicio personalizado
      }
    };

    // Métodos para compartir - VERSIÓN MEJORADA
    const shareByWhatsApp = async () => {
      try {
        // Mostrar un indicador de carga
        await alert('Procesando imágenes para compartir...'); // Reemplazado: alert nativo por servicio personalizado

        // Verificar el tamaño del menú
        if (menuItems.value.length > 30) {
          await alert('El menú tiene demasiados elementos. Por favor, seleccione menos platos para compartir.'); // Reemplazado: alert nativo por servicio personalizado
          return;
        }

        // Preparar una copia de los items más ligera para compartir
        const itemsToShare = menuItems.value.map(item => ({
          id: item.id,
          name: item.name,
          description: item.description,
          price: item.price,
          includesDrink: item.includesDrink,
          // No incluir availableQuantity para menús compartidos
          image: item.image // La imagen será procesada/comprimida en saveMenu
        }));
        console.log(`Preparando para compartir ${itemsToShare.length} elementos...`);
        const menuId = await saveMenu(itemsToShare);
        const url = `${window.location.origin}/menu/${menuId}`;
        shareLink.value = url;
        const message = formatShareLinkMessage(url, businessInfo.value.name);
        const whatsappUrl = `https://api.whatsapp.com/send?text=${encodeURIComponent(message)}`;
        window.open(whatsappUrl, '_blank');
      } catch (error) {
        console.error('Error al compartir por WhatsApp:', error);
        await alert(`Error al compartir el menú: ${error.message || 'Error desconocido'}. Por favor, intenta reducir el tamaño de las imágenes o eliminar algunos ítems.`); // Reemplazado: alert nativo por servicio personalizado
      }
    };
    const copyLink = async () => {
      try {
        // Mostrar un indicador de carga
        const loadingMessage = 'Procesando imágenes para compartir...';
        showCopiedMessage.value = true;

        // Verificar el tamaño del menú
        if (menuItems.value.length > 30) {
          await alert('El menú tiene demasiados elementos. Por favor, seleccione menos platos para compartir.'); // Reemplazado: alert nativo por servicio personalizado
          showCopiedMessage.value = false;
          return;
        }

        // Preparar una copia de los items más ligera para compartir
        const itemsToShare = menuItems.value.map(item => ({
          id: item.id,
          name: item.name,
          description: item.description,
          price: item.price,
          includesDrink: item.includesDrink,
          // No incluir availableQuantity para menús compartidos
          image: item.image // La imagen será procesada/comprimida en saveMenu
        }));
        const menuId = await saveMenu(itemsToShare);
        const url = `${window.location.origin}/menu/${menuId}`;
        shareLink.value = url;
        try {
          await navigator.clipboard.writeText(url);
          showCopiedMessage.value = true;
          setTimeout(() => {
            showCopiedMessage.value = false;
          }, 3000);
        } catch (clipboardError) {
          console.error('Error al copiar al portapapeles:', clipboardError);
          // Ofrecer forma manual de copiar con nuestro servicio de diálogo
          await alert('No se pudo copiar automáticamente. Por favor, copia manualmente este enlace: ' + url); // Reemplazado: prompt nativo por servicio personalizado
        }
      } catch (error) {
        console.error('Error al generar enlace para compartir:', error);
        showCopiedMessage.value = false;
        await alert(`Error al generar el enlace: ${error.message || 'Error desconocido'}. Por favor, intenta reducir el tamaño de las imágenes o eliminar algunos ítems.`); // Reemplazado: alert nativo por servicio personalizado
      }
    };

    // Computados
    const totalSales = computed(() => {
      return soldItems.value.reduce((total, item) => {
        return total + item.quantity * item.price;
      }, 0);
    });

    // Formateo de precios con separador de miles (punto) y decimales (coma)
    const formatPrice = price => {
      // Asegurarse que price es un número
      if (isNaN(Number(price))) return '0,00';

      // Convertir a número, fijar 2 decimales y convertir a string
      const fixed = Number(price).toFixed(2);

      // Separar parte entera y decimal
      const [intPart, decPart] = fixed.split('.');

      // Formatear parte entera con puntos como separadores de miles
      const formattedIntPart = intPart.replace(/\B(?=(\d{3})+(?!\d))/g, '.');

      // Unir con coma como separador decimal
      return `${formattedIntPart},${decPart}`;
    };

    // Función para formatear fechas
    const formatDate = dateStr => {
      if (!dateStr) return 'N/A';
      const date = new Date(dateStr);
      if (isNaN(date.getTime())) return 'Fecha inválida';
      return new Intl.DateTimeFormat('es-ES', {
        day: '2-digit',
        month: '2-digit',
        year: 'numeric',
        hour: '2-digit',
        minute: '2-digit'
      }).format(date);
    };

    // Filtrado de platos vendidos
    const filteredSoldItems = computed(() => {
      let items = soldItems.value;

      // Si items no es un array, devolver un array vacío
      if (!Array.isArray(items)) {
        console.warn('soldItems no es un array. Devolviendo array vacío.');
        return [];
      }

      // Filtrar por término de búsqueda
      if (searchTerm.value) {
        const term = searchTerm.value.toLowerCase();
        items = items.filter(item => {
          // Validar que item.name existe antes de usar toLowerCase()
          return item && item.name && typeof item.name === 'string' && item.name.toLowerCase().includes(term);
        });
      }

      // Filtrar por categoría de forma segura
      if (categoryFilter.value) {
        if (categoryFilter.value === 'drinks') {
          items = items.filter(item => Boolean(item && item.includesDrink));
        } else if (categoryFilter.value === 'nodrinks') {
          // Corregir aquí - asegurar que item está definido
          items = items.filter(item => item && item.includesDrink === false);
        }
      }

      // Ordenar según el criterio seleccionado
      if (sortBy.value) {
        items = [...items]; // Crear una copia para no mutar el original

        switch (sortBy.value) {
          case 'name':
            items.sort((a, b) => {
              // Validar que a.name y b.name existen
              if (!a || !a.name) return 1;
              if (!b || !b.name) return -1;
              return a.name.localeCompare(b.name);
            });
            break;
          case 'quantity-desc':
            items.sort((a, b) => {
              // Validar que a.quantity y b.quantity son números
              const qtyA = a && typeof a.quantity === 'number' ? a.quantity : 0;
              const qtyB = b && typeof b.quantity === 'number' ? b.quantity : 0;
              return qtyB - qtyA;
            });
            break;
          case 'quantity-asc':
            items.sort((a, b) => {
              const qtyA = a && typeof a.quantity === 'number' ? a.quantity : 0;
              const qtyB = b && typeof b.quantity === 'number' ? b.quantity : 0;
              return qtyA - qtyB;
            });
            break;
          case 'price-desc':
            items.sort((a, b) => {
              const priceA = a && typeof a.price === 'number' ? a.price : 0;
              const priceB = b && typeof b.price === 'number' ? b.price : 0;
              return priceB - priceA;
            });
            break;
          case 'price-asc':
            items.sort((a, b) => {
              const priceA = a && typeof a.price === 'number' ? a.price : 0;
              const priceB = b && typeof b.price === 'number' ? b.price : 0;
              return priceA - priceB;
            });
            break;
          case 'date-desc':
            items.sort((a, b) => {
              const dateA = a && a.date ? new Date(a.date) : new Date(0);
              const dateB = b && b.date ? new Date(b.date) : new Date(0);
              return dateB - dateA; // De más reciente a más antiguo
            });
            break;
          case 'date-asc':
            items.sort((a, b) => {
              const dateA = a && a.date ? new Date(a.date) : new Date(0);
              const dateB = b && b.date ? new Date(b.date) : new Date(0);
              return dateA - dateB; // De más antiguo a más reciente
            });
            break;
          default:
            break;
        }
      }
      return items;
    });

    // Exportar datos
    const exportToExcel = () => {
      // Crear un array con los datos a exportar
      const data = [['Nombre del Plato', 'Categoría', 'Cantidad Vendida', 'Precio por Unidad', 'Fecha de Venta', 'Subtotal'], ...filteredSoldItems.value.map(item => [item.name, item.includesDrink ? 'Con refresco' : 'Sin refresco', item.quantity, `$${formatPrice(item.price)}`, formatDate(item.date), `$${formatPrice(item.quantity * item.price)}`]), ['', '', '', '', 'Total', `$${formatPrice(totalSales.value)}`]];

      // Crear un libro de Excel y una hoja de cálculo
      const ws = XLSX.utils.aoa_to_sheet(data);
      const wb = XLSX.utils.book_new();
      XLSX.utils.book_append_sheet(wb, ws, 'Ventas');

      // Generar el archivo Excel y descargarlo
      const excelBuffer = XLSX.write(wb, {
        bookType: 'xlsx',
        type: 'array'
      });
      const blob = new Blob([excelBuffer], {
        type: 'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet'
      });
      saveAs(blob, `ventas_${new Date().toLocaleDateString()}.xlsx`);
    };
    const exportToPDF = () => {
      // Crear un nuevo documento PDF
      const doc = new jsPDF();

      // Añadir título
      doc.setFontSize(18);
      doc.text('Reporte de Ventas', 14, 22);
      doc.setFontSize(11);
      doc.text(`Fecha: ${new Date().toLocaleDateString()}`, 14, 32);

      // Crear la tabla con los datos
      const tableData = filteredSoldItems.value.map(item => [item.name, item.includesDrink ? 'Con refresco' : 'Sin refresco', item.quantity, `$${formatPrice(item.price)}`, formatDate(item.date), `$${formatPrice(item.quantity * item.price)}`]);

      // Añadir la tabla
      doc.autoTable({
        head: [['Nombre del Plato', 'Categoría', 'Cantidad Vendida', 'Precio por Unidad', 'Fecha de Venta', 'Subtotal']],
        body: tableData,
        foot: [['', '', '', '', 'Total', `$${formatPrice(totalSales.value)}`]],
        startY: 40,
        theme: 'striped',
        headStyles: {
          fillColor: [76, 175, 80]
        },
        footStyles: {
          fillColor: [240, 240, 240],
          textColor: [0, 0, 0],
          fontStyle: 'bold'
        }
      });

      // Guardar el PDF de exportación
      doc.save(`ventas_${new Date().toLocaleDateString()}.pdf`);
    };
    return {
      // Negocio
      businessInfo,
      showBusinessForm,
      toggleBusinessForm,
      onLogoChange,
      saveBusinessInfo,
      clearBusinessForm,
      // Información de Pago
      paymentInfo,
      showPaymentForm,
      togglePaymentForm,
      onQRImageChange,
      onNequiImageChange,
      savePaymentInfo,
      clearPaymentForm,
      // Menú
      newItem,
      menuItems,
      showMenuForm,
      isEditing,
      toggleMenuForm,
      onImageChange,
      saveMenuItem,
      editMenuItem,
      deleteMenuItem,
      sellMenuItem,
      clearMenuForm,
      // Menú especial
      newSpecialItem,
      specialMenuItems,
      showSpecialMenuForm,
      toggleSpecialMenuForm,
      onSpecialImageChange,
      saveSpecialMenuItem,
      clearSpecialMenuForm,
      editSpecialMenuItem,
      deleteSpecialMenuItem,
      sellSpecialMenuItem,
      // Ventas
      soldItems,
      totalSales,
      searchTerm,
      sortBy,
      categoryFilter,
      filteredSoldItems,
      formatPrice,
      selectedChartType,
      // Métodos de exportación
      exportToExcel,
      exportToPDF,
      // Compartir
      shareLink,
      showCopiedMessage,
      shareByWhatsApp,
      copyLink,
      // Estado de migración
      migrationStatus,
      // Función para eliminar duplicados
      removeDuplicates,
      // Errores del gráfico
      chartError,
      handleChartError,
      // Notificaciones
      notification,
      formatDate
    };
  }
};
