    const businessInfo = ref({
      id: 'business_info_1',
      name: '',
      description: '',
      logo: '',
      paymentInfo: {
        qrTitle: '',
        qrImage: '',
        nequiNumber: '',
        nequiImage: ''
      }
    });
    const showBusinessForm = ref(false);

    // Estado para información de pago
    const paymentInfo = ref({
      qrTitle: '',
      qrImage: '',
      nequiNumber: '',
      nequiImage: ''
    });
    const showPaymentForm = ref(false);

    // Estado para gestión de inventario
    const inventoryItems = ref([]);
    const inventoryFilter = ref('all');
    const showInventorySection = ref(false);

    // Propiedad computada para filtrar items de inventario
    const filteredInventoryItems = computed(() => {
      if (inventoryFilter.value === 'all') {
        return inventoryItems.value;
      } else if (inventoryFilter.value === 'available') {
        return inventoryItems.value.filter(item => item.availableQuantity > 0);
      } else {
        return inventoryItems.value.filter(item => item.availableQuantity <= 0);
      }
    });

    // Estado para categorías
    const categories = ref([]);
    const showCategoriesSection = ref(false);
    const newCategory = ref({
      name: '',
      description: ''
    });
    const showAddCategoryForm = ref(false);

    // Estado para notificaciones
    const notification = ref({
      show: false,
      message: '',
      type: 'success',
      timeout: null
    });

    // Cargar datos al montar el componente
    onMounted(async () => {
      try {
        // Cargar información del negocio desde IndexedDB
        const {
          getBusinessInfoFromDB
        } = await import('../services/storageService');
        const storedBusinessInfo = await getBusinessInfoFromDB();
        if (storedBusinessInfo) {
          businessInfo.value = storedBusinessInfo;

          // Si hay información de pago, cargarla en el estado correspondiente
          if (storedBusinessInfo.paymentInfo) {
            paymentInfo.value = {
              qrTitle: storedBusinessInfo.paymentInfo.qrTitle || '',
              qrImage: storedBusinessInfo.paymentInfo.qrImage || '',
              nequiNumber: storedBusinessInfo.paymentInfo.nequiNumber || '',
              nequiImage: storedBusinessInfo.paymentInfo.nequiImage || ''
            };
          }
        }

        // Cargar inventario y categorías
        const {
          getInventoryItems,
          getCategories
        } = await import('../services/menuService');
        const items = await getInventoryItems();
        if (items && items.length > 0) {
          inventoryItems.value = items;
        }
        const cats = await getCategories();
        if (cats && cats.length > 0) {
          categories.value = cats;
        }
        console.log('Datos cargados correctamente');
      } catch (error) {
        console.error('Error al cargar datos:', error);
        notification.value = {
          show: true,
          message: 'Error al cargar datos. Por favor, intente nuevamente.',
          type: 'error',
          timeout: setTimeout(() => {
            notification.value.show = false;
          }, 3000)
        };
      }
    });

    // Métodos para información del negocio
    const toggleBusinessForm = () => {
      showBusinessForm.value = !showBusinessForm.value;
    };
    const onLogoChange = event => {
      const file = event.target.files[0];
      if (file) {
        const reader = new FileReader();
        reader.onload = e => {
          businessInfo.value.logo = e.target.result;
        };
        reader.readAsDataURL(file);
      }
    };
    const saveBusinessInfo = async () => {
      try {
        if (notification.value.timeout) {
          clearTimeout(notification.value.timeout);
        }
        await saveBusinessInfoToDB(businessInfo.value);
        notification.value = {
          show: true,
          message: 'Información del negocio guardada con éxito!',
          type: 'success',
          timeout: setTimeout(() => {
            notification.value.show = false;
          }, 3000)
        };
      } catch (error) {
        console.error('Error al guardar información del negocio:', error);
        notification.value = {
          show: true,
          message: 'Error al guardar información del negocio',
          type: 'error',
          timeout: setTimeout(() => {
            notification.value.show = false;
          }, 3000)
        };
      }
    };
    const clearBusinessForm = () => {
      businessInfo.value = {
        id: 'business_info_1',
        name: '',
        description: '',
        logo: '',
        paymentInfo: {
          qrTitle: '',
          qrImage: '',
          nequiNumber: '',
          nequiImage: ''
        }
      };
    };

    // Métodos para información de pago
    const togglePaymentForm = () => {
      showPaymentForm.value = !showPaymentForm.value;
    };
    const onQRImageChange = event => {
      const file = event.target.files[0];
      if (file) {
        const reader = new FileReader();
        reader.onload = e => {
          paymentInfo.value.qrImage = e.target.result;
        };
        reader.readAsDataURL(file);
      }
    };
    const onNequiImageChange = event => {
      const file = event.target.files[0];
      if (file) {
        const reader = new FileReader();
        reader.onload = e => {
          paymentInfo.value.nequiImage = e.target.result;
        };
        reader.readAsDataURL(file);
      }
    };
    const savePaymentInfo = async () => {
      try {
        if (notification.value.timeout) {
          clearTimeout(notification.value.timeout);
        }
        if (!businessInfo.value.paymentInfo) {
          businessInfo.value.paymentInfo = {};
        }
        businessInfo.value.paymentInfo.qrTitle = paymentInfo.value.qrTitle;
        businessInfo.value.paymentInfo.qrImage = paymentInfo.value.qrImage;
        businessInfo.value.paymentInfo.nequiNumber = paymentInfo.value.nequiNumber;
        businessInfo.value.paymentInfo.nequiImage = paymentInfo.value.nequiImage;
        await saveBusinessInfoToDB(businessInfo.value);

        // Sincronizar con el backend
        try {
          // Importar dinámicamente para evitar dependencias circulares
          const {
            syncBusinessInfoWithBackend
          } = await import('../services/menuService');
          await syncBusinessInfoWithBackend(businessInfo.value);
          console.log('Información de pago sincronizada con el backend');
        } catch (syncError) {
          console.error('Error al sincronizar con el backend:', syncError);
          // No mostrar error al usuario, solo registrar en consola
        }
        notification.value = {
          show: true,
          message: 'Información de pago guardada con éxito!',
          type: 'success',
          timeout: setTimeout(() => {
            notification.value.show = false;
          }, 3000)
        };
      } catch (error) {
        console.error('Error al guardar información de pago:', error);
        notification.value = {
          show: true,
          message: 'Error al guardar información de pago',
          type: 'error',
          timeout: setTimeout(() => {
            notification.value.show = false;
          }, 3000)
        };
      }
    };
    const clearPaymentForm = () => {
      paymentInfo.value = {
        qrTitle: '',
        qrImage: '',
        nequiNumber: '',
        nequiImage: ''
      };
    };

    // Métodos para gestión de inventario
    const toggleInventorySection = () => {
      showInventorySection.value = !showInventorySection.value;
    };
    const editInventoryItem = async item => {
      try {
        // Aquí se implementaría un modal o formulario para editar el item
        console.log('Editando item:', item);

        // Simulación de edición (en una implementación real, esto sería un formulario)
        const updatedItem = {
          ...item,
          updatedAt: new Date().toISOString()
        };

        // Actualizar el item en el array local
        const index = inventoryItems.value.findIndex(i => i.id === item.id);
        if (index !== -1) {
          inventoryItems.value[index] = updatedItem;
        }

        // Guardar en IndexedDB
        const {
          saveInventoryItemToDB
        } = await import('../services/storageService');
        await saveInventoryItemToDB(updatedItem);

        // Sincronizar con el backend
        try {
          const {
            syncInventoryItemWithBackend
          } = await import('../services/menuService');
          await syncInventoryItemWithBackend(updatedItem);
          console.log('Item de inventario sincronizado con el backend');
          notification.value = {
            show: true,
            message: 'Item actualizado con éxito!',
            type: 'success',
            timeout: setTimeout(() => {
              notification.value.show = false;
            }, 3000)
          };
        } catch (syncError) {
          console.error('Error al sincronizar item con el backend:', syncError);
          notification.value = {
            show: true,
            message: 'Item actualizado localmente, pero hubo un error al sincronizar con el servidor',
            type: 'warning',
            timeout: setTimeout(() => {
              notification.value.show = false;
            }, 3000)
          };
        }
      } catch (error) {
        console.error('Error al editar item:', error);
        notification.value = {
          show: true,
          message: 'Error al actualizar el item',
          type: 'error',
          timeout: setTimeout(() => {
            notification.value.show = false;
          }, 3000)
        };
      }
    };
    const toggleItemAvailability = async item => {
      try {
        // Cambiar la disponibilidad del item
        const updatedItem = {
          ...item,
          availableQuantity: item.availableQuantity > 0 ? 0 : 10,
          updatedAt: new Date().toISOString()
        };

        // Actualizar el item en el array local
        const index = inventoryItems.value.findIndex(i => i.id === item.id);
        if (index !== -1) {
          inventoryItems.value[index] = updatedItem;
        }

        // Guardar en IndexedDB
        const {
          saveInventoryItemToDB
        } = await import('../services/storageService');
        await saveInventoryItemToDB(updatedItem);

        // Sincronizar con el backend
        try {
          const {
            syncInventoryItemWithBackend
          } = await import('../services/menuService');
          await syncInventoryItemWithBackend(updatedItem);
          console.log('Disponibilidad del item sincronizada con el backend');
          notification.value = {
            show: true,
            message: `Item ${updatedItem.availableQuantity > 0 ? 'disponible' : 'no disponible'} actualizado con éxito!`,
            type: 'success',
            timeout: setTimeout(() => {
              notification.value.show = false;
            }, 3000)
          };
        } catch (syncError) {
          console.error('Error al sincronizar disponibilidad con el backend:', syncError);
          notification.value = {
            show: true,
            message: 'Disponibilidad actualizada localmente, pero hubo un error al sincronizar con el servidor',
            type: 'warning',
            timeout: setTimeout(() => {
              notification.value.show = false;
            }, 3000)
          };
        }
      } catch (error) {
        console.error('Error al cambiar disponibilidad:', error);
        notification.value = {
          show: true,
          message: 'Error al actualizar la disponibilidad',
          type: 'error',
          timeout: setTimeout(() => {
            notification.value.show = false;
          }, 3000)
        };
      }
    };

    // Métodos para categorías
    const toggleCategoriesSection = () => {
      showCategoriesSection.value = !showCategoriesSection.value;
    };
    const editCategory = category => {
      console.log('Editar categoría:', category);
    };
    const deleteCategory = async category => {
      try {
        // Confirmar la eliminación (en una implementación real, esto sería un modal de confirmación)
        const confirmDelete = confirm(`¿Está seguro de eliminar la categoría "${category.name}"?`);
        if (!confirmDelete) return;

        // Eliminar la categoría del array local
        categories.value = categories.value.filter(c => c.id !== category.id);

        // Eliminar de IndexedDB
        const {
          deleteCategoryFromDB
        } = await import('../services/storageService');
        await deleteCategoryFromDB(category.id);

        // Sincronizar con el backend
        try {
          const {
            deleteCategoryFromBackend
          } = await import('../services/menuService');
          await deleteCategoryFromBackend(category.id);
          console.log('Categoría eliminada del backend');
          notification.value = {
            show: true,
            message: 'Categoría eliminada con éxito!',
            type: 'success',
            timeout: setTimeout(() => {
              notification.value.show = false;
            }, 3000)
          };
        } catch (syncError) {
          console.error('Error al eliminar categoría del backend:', syncError);
          notification.value = {
            show: true,
            message: 'Categoría eliminada localmente, pero hubo un error al sincronizar con el servidor',
            type: 'warning',
            timeout: setTimeout(() => {
              notification.value.show = false;
            }, 3000)
          };
        }
      } catch (error) {
        console.error('Error al eliminar categoría:', error);
        notification.value = {
          show: true,
          message: 'Error al eliminar la categoría',
          type: 'error',
          timeout: setTimeout(() => {
            notification.value.show = false;
          }, 3000)
        };
      }
    };
    const saveNewCategory = async () => {
      try {
        if (notification.value.timeout) {
          clearTimeout(notification.value.timeout);
        }

        // Validar que la categoría tenga un nombre
        if (!newCategory.value.name.trim()) {
          notification.value = {
            show: true,
            message: 'El nombre de la categoría es obligatorio',
            type: 'error',
            timeout: setTimeout(() => {
              notification.value.show = false;
            }, 3000)
          };
          return;
        }

        // Crear un ID único para la nueva categoría
        const newCategoryWithId = {
          ...newCategory.value,
          id: 'category_' + Date.now(),
          createdAt: new Date().toISOString()
        };

        // Añadir la categoría al array local
        categories.value.push(newCategoryWithId);

        // Guardar en IndexedDB
        const {
          saveCategoryToDB
        } = await import('../services/storageService');
        await saveCategoryToDB(newCategoryWithId);

        // Sincronizar con el backend
        try {
          const {
            syncCategoryWithBackend
          } = await import('../services/menuService');
          await syncCategoryWithBackend(newCategoryWithId);
          console.log('Categoría sincronizada con el backend');
        } catch (syncError) {
          console.error('Error al sincronizar categoría con el backend:', syncError);
          // No mostrar error al usuario, solo registrar en consola
        }

        // Limpiar el formulario
        clearCategoryForm();

        // Mostrar notificación de éxito
        notification.value = {
          show: true,
          message: 'Categoría guardada con éxito!',
          type: 'success',
          timeout: setTimeout(() => {
            notification.value.show = false;
          }, 3000)
        };
      } catch (error) {
        console.error('Error al guardar categoría:', error);
        notification.value = {
          show: true,
          message: 'Error al guardar categoría',
          type: 'error',
          timeout: setTimeout(() => {
            notification.value.show = false;
          }, 3000)
        };
      }
    };
    const clearCategoryForm = () => {
      newCategory.value = {
        name: '',
        description: ''
      };
    };
    return {
      businessInfo,
      showBusinessForm,
      toggleBusinessForm,
      onLogoChange,
      saveBusinessInfo,
      clearBusinessForm,
      paymentInfo,
      showPaymentForm,
      togglePaymentForm,
      onQRImageChange,
      onNequiImageChange,
      savePaymentInfo,
      clearPaymentForm,
      inventoryItems,
      inventoryFilter,
      showInventorySection,
      toggleInventorySection,
      editInventoryItem,
      toggleItemAvailability,
      filteredInventoryItems,
      categories,
      showCategoriesSection,
      toggleCategoriesSection,
      editCategory,
      deleteCategory,
      newCategory,
      showAddCategoryForm,
      saveNewCategory,
      clearCategoryForm,
      notification
    };
  }
};
