import { createApp } from 'vue'
import App from './App.vue'
import router from './router'

// Configuración global para Vue para manejo de errores
const app = createApp(App);

// Configurar handler global de errores
app.config.errorHandler = (err, instance, info) => {
  console.warn('Vue Error Handler:', err, info);
  // Puedes enviar el error a un servicio de seguimiento como Sentry aquí
};

// Configurar handler para errores de componentes
app.config.warnHandler = (msg, instance, trace) => {
  console.warn('Vue Warning Handler:', msg, trace);
  // Manejo personalizado de advertencias
};

// Inicializar la base de datos antes de montar la aplicación
import { openDatabase } from './services/dbInitializer';

// Intentar inicializar la base de datos antes de montar la app
openDatabase()
  .then(() => {
    console.log("Base de datos inicializada correctamente");
    app.use(router);
    app.mount('#app');
  })
  .catch(error => {
    console.error("Error al inicializar la base de datos:", error);
    // Mostrar un mensaje al usuario
    document.body.innerHTML = `
      <div style="padding: 20px; text-align: center; margin-top: 100px;">
        <h1>Error al inicializar la base de datos</h1>
        <p>${error.message || 'Error desconocido'}</p>
        <button onclick="window.location.reload()">Intentar nuevamente</button>
      </div>
    `;
  });
