          console.log('‚úÖ Lista de reservas actualizada:', reservations.value.length);

          // Mostrar notificaci√≥n si se recibi√≥ una nueva reserva desde WhatsApp
          const whatsappReservations = reservations.value.filter(r => r.source === 'whatsapp');
