import { ref, computed, onMounted } from 'vue';
import { saveMenu } from '../services/menuService';
import { formatShareLinkMessage } from '../utils/messageFormatter';
import SalesChartComponent from './SalesChartComponent.vue';
import { saveAs } from 'file-saver';
import * as XLSX from 'xlsx';
import { jsPDF } from 'jspdf';
import 'jspdf-autotable';
import { getMenuItems, saveMenuItems, getBusinessInfo, saveBusinessInfo, getSoldItems, saveSoldItems, deleteMenuItem, migrateFromLocalStorage, compressImage } from '../services/storageService';
export default {
  name: 'MenuComponent',
  components: {
    SalesChartComponent
  },
  setup() {
    // Estado para informaci√≥n del negocio
    const businessInfo = ref({
      name: '',
      description: '',
      contact: '',
      address: '',
      logo: ''
    });
    const showBusinessForm = ref(false);

    // Estado para platos del men√∫
    const newItem = ref({
      name: '',
      description: '',
      price: 0,
      includesDrink: false,
      availableQuantity: 0,
      image: '',
      id: null
    });
    const menuItems = ref([]);
    const showMenuForm = ref(false);
    const isEditing = ref(false);
    const editingIndex = ref(-1);

    // Estado para ventas e inventario
    const soldItems = ref([]);
    const searchTerm = ref('');
    const sortBy = ref('');
    const categoryFilter = ref('');
    const selectedChartType = ref('bar');

    // Estado para compartir
    const shareLink = ref('');
    const showCopiedMessage = ref(false);

    // Estado para mostrar progreso de migraci√≥n
    const migrationStatus = ref({
      inProgress: false,
      completed: false,
      message: ''
    });

    // Estado para errores del gr√°fico
    const chartError = ref(null);

    // Manejar errores del gr√°fico
    const handleChartError = error => {
      console.error("Error en el gr√°fico:", error);
      chartError.value = error;
    };

    // Cargar datos guardados
    onMounted(async () => {
      try {
        // Mostrar estado de carga
        migrationStatus.value.inProgress = true;
        migrationStatus.value.message = 'Cargando datos...';

        // Primero intentar migrar desde localStorage si existen datos
        const hasLocalData = localStorage.getItem('businessInfo') || localStorage.getItem('menuItems') || localStorage.getItem('soldItems');
        if (hasLocalData) {
          migrationStatus.value.message = 'Migrando datos desde localStorage a IndexedDB...';
          const migrationResult = await migrateFromLocalStorage();
          if (migrationResult) {
            migrationStatus.value.completed = true;
            migrationStatus.value.message = 'Migraci√≥n completada exitosamente';

            // Limpiar localStorage despu√©s de migrar exitosamente
            localStorage.removeItem('businessInfo');
            localStorage.removeItem('menuItems');
            localStorage.removeItem('soldItems');
          } else {
            migrationStatus.value.message = 'Error en la migraci√≥n de datos';
          }
        }

        // Cargar datos desde IndexedDB (siempre, independientemente de si hubo migraci√≥n)
        const dbBusinessInfo = await getBusinessInfo();
        if (dbBusinessInfo && dbBusinessInfo.name) {
          businessInfo.value = dbBusinessInfo;
        }

        // Cargar men√∫ desde IndexedDB
        const dbMenuItems = await getMenuItems();
        if (dbMenuItems && dbMenuItems.length > 0) {
          // Asegurarse de eliminar duplicados usando IDs √∫nicos
          const uniqueItems = removeDuplicates(dbMenuItems);
          menuItems.value = uniqueItems;

          // Si hubo diferencias, guardar la lista corregida de vuelta en IndexedDB
          if (uniqueItems.length < dbMenuItems.length) {
            console.log(`Se eliminaron ${dbMenuItems.length - uniqueItems.length} elementos duplicados del men√∫`);
            await saveMenuItems(uniqueItems);
          }
        }

        // Cargar ventas desde IndexedDB
        const dbSoldItems = await getSoldItems();
        if (dbSoldItems && dbSoldItems.length > 0) {
          soldItems.value = dbSoldItems;
        }

        // Finalizar estado de carga
        setTimeout(() => {
          migrationStatus.value.inProgress = false;
        }, 1500);
      } catch (error) {
        console.error('Error al cargar datos:', error);
        migrationStatus.value.message = 'Error al cargar datos: ' + error.message;
        setTimeout(() => {
          migrationStatus.value.inProgress = false;
        }, 3000);
      }
    });

    // Funci√≥n para eliminar duplicados basados en ID
    const removeDuplicates = items => {
      const seen = new Map();
      return items.filter(item => {
        // Si el elemento no tiene ID, generamos uno
        if (!item.id) {
          item.id = Date.now() + Math.random().toString(36).substring(2, 10);
        }

        // Si ya hemos visto este ID, es un duplicado
        if (seen.has(item.id)) {
          return false;
        }

        // De lo contrario, marcamos como visto y lo conservamos
        seen.set(item.id, true);
        return true;
      });
    };

    // M√©todos para informaci√≥n del negocio
    const toggleBusinessForm = () => {
      showBusinessForm.value = !showBusinessForm.value;
    };
    const onLogoChange = async event => {
      const file = event.target.files[0];
      if (file) {
        const reader = new FileReader();
        reader.onload = async e => {
          // Comprimir la imagen antes de guardarla
          const compressedLogo = await compressImage(e.target.result);
          businessInfo.value.logo = compressedLogo;
        };
        reader.readAsDataURL(file);
      }
    };
    const saveBusinessInfo = async () => {
      try {
        await saveBusinessInfo(businessInfo.value);
        alert('Informaci√≥n del negocio guardada con √©xito!');
      } catch (error) {
        console.error('Error al guardar informaci√≥n del negocio:', error);
        alert('Error al guardar la informaci√≥n. Por favor, intenta nuevamente.');
      }
    };
    const clearBusinessForm = () => {
      if (confirm('¬øEst√°s seguro de querer limpiar el formulario?')) {
        businessInfo.value = {
          name: '',
          description: '',
          contact: '',
          address: '',
          logo: ''
        };
      }
    };

    // M√©todos para platos del men√∫
    const toggleMenuForm = () => {
      showMenuForm.value = !showMenuForm.value;
      if (!showMenuForm.value) {
        clearMenuForm();
      }
    };
    const onImageChange = async event => {
      const file = event.target.files[0];
      if (file) {
        const reader = new FileReader();
        reader.onload = async e => {
          // Comprimir la imagen antes de guardarla
          const compressedImage = await compressImage(e.target.result);
          newItem.value.image = compressedImage;
        };
        reader.readAsDataURL(file);
      }
    };
    const saveMenuItem = async () => {
      try {
        if (!newItem.value.id) {
          newItem.value.id = Date.now() + Math.random().toString(36).substring(2, 10);
        }
        if (isEditing.value) {
          // Actualizar plato existente
          const index = menuItems.value.findIndex(item => item.id === newItem.value.id);
          if (index !== -1) {
            menuItems.value[index] = {
              ...newItem.value
            };
          }
          isEditing.value = false;
          editingIndex.value = -1;
          alert('Plato actualizado con √©xito!');
        } else {
          // Verificar si ya existe un plato con el mismo nombre
          const existingItem = menuItems.value.find(item => item.name.toLowerCase() === newItem.value.name.toLowerCase());
          if (existingItem) {
            if (confirm(`Ya existe un plato llamado "${newItem.value.name}". ¬øDesea actualizarlo?`)) {
              // Actualizar el plato existente pero conservar su ID
              const index = menuItems.value.findIndex(item => item.id === existingItem.id);
              if (index !== -1) {
                newItem.value.id = existingItem.id; // Mantener el ID original
                menuItems.value[index] = {
                  ...newItem.value
                };
                alert('Plato actualizado con √©xito!');
              }
            } else {
              // Usuario decidi√≥ no sobrescribir, no hacemos nada
              return;
            }
          } else {
            // Crear nuevo plato
            menuItems.value.push({
              ...newItem.value
            });
            alert('Plato creado con √©xito!');
          }
        }

        // Guardar en IndexedDB
        await saveMenuItems(menuItems.value);

        // Limpiar formulario
        clearMenuForm();
      } catch (error) {
        console.error('Error al guardar el plato:', error);
        alert('Error al guardar el plato. Por favor, intenta nuevamente.');
      }
    };
    const editMenuItem = index => {
      newItem.value = {
        ...menuItems.value[index]
      };
      isEditing.value = true;
      editingIndex.value = index;
      showMenuForm.value = true;
    };
    const deleteMenuItem = async index => {
      if (confirm('¬øEst√°s seguro de querer eliminar este plato?')) {
        try {
          const item = menuItems.value[index];

          // Agregar a vendidos antes de eliminar (simulaci√≥n)
          const soldItem = soldItems.value.find(sold => sold.name === item.name);
          if (soldItem) {
            soldItem.quantity += 1;
          } else {
            // Crear un objeto limpio y serializable para el item vendido
            const newSoldItem = {
              name: item.name || 'Producto sin nombre',
              quantity: 1,
              price: Number(item.price) || 0,
              includesDrink: Boolean(item.includesDrink)
            };
            soldItems.value.push(newSoldItem);
          }

          // Para garantizar que todos los elementos son serializables
          const cleanSoldItems = soldItems.value.map(item => ({
            name: String(item.name || 'Producto desconocido'),
            quantity: Number(item.quantity) || 1,
            price: Number(item.price) || 0,
            includesDrink: Boolean(item.includesDrink)
          }));

          // Guardar vendidos en IndexedDB
          await saveSoldItems(cleanSoldItems);

          // Eliminar del men√∫ en IndexedDB
          if (item.id) {
            await deleteMenuItem(item.id);
          }

          // Eliminar del array local
          menuItems.value.splice(index, 1);

          // Actualizar tambi√©n la versi√≥n local de soldItems
          soldItems.value = cleanSoldItems;
        } catch (error) {
          console.error('Error al eliminar el plato:', error);
          alert('Error al eliminar el plato. Por favor, intenta nuevamente.');
        }
      }
    };
    const sellMenuItem = async index => {
      try {
        const item = menuItems.value[index];

        // Verificar si hay unidades disponibles
        if (item.availableQuantity <= 0) {
          alert('No hay unidades disponibles de este plato.');
          return;
        }

        // Reducir la cantidad disponible
        item.availableQuantity--;

        // Actualizar lista de platos vendidos
        const soldItem = soldItems.value.find(sold => sold.name === item.name);
        if (soldItem) {
          soldItem.quantity += 1;
        } else {
          // Crear un objeto limpio y serializable para el item vendido
          const newSoldItem = {
            name: item.name || 'Producto sin nombre',
            quantity: 1,
            price: Number(item.price) || 0,
            includesDrink: Boolean(item.includesDrink)
          };
          soldItems.value.push(newSoldItem);
        }

        // Para garantizar que todos los elementos son serializables
        const cleanSoldItems = soldItems.value.map(item => ({
          name: String(item.name || 'Producto desconocido'),
          quantity: Number(item.quantity) || 1,
          price: Number(item.price) || 0,
          includesDrink: Boolean(item.includesDrink)
        }));

        // Guardar cambios en IndexedDB
        await saveMenuItems(menuItems.value);
        await saveSoldItems(cleanSoldItems);

        // Actualizar tambi√©n la versi√≥n local de soldItems
        soldItems.value = cleanSoldItems;
      } catch (error) {
        console.error('Error al vender el plato:', error);
        alert('Error al procesar la venta. Por favor, intenta nuevamente.');
      }
    };
    const clearMenuForm = () => {
      newItem.value = {
        name: '',
        description: '',
        price: 0,
        includesDrink: false,
        availableQuantity: 0,
        image: '',
        id: null
      };
      isEditing.value = false;
      editingIndex.value = -1;
    };

    // M√©todos para compartir
    const shareByWhatsApp = async () => {
      try {
        // No modificar los items antes de enviarlos, para mantener las im√°genes
        const itemsToShare = [...menuItems.value]; // Copia simple para no afectar el estado original

        console.log(`Compartiendo ${itemsToShare.length} elementos, ${itemsToShare.filter(i => Boolean(i.image)).length} con imagen`);
        const menuId = await saveMenu(itemsToShare);
        const url = `${window.location.origin}/menu/${menuId}`;
        shareLink.value = url;
        const message = formatShareLinkMessage(url, businessInfo.value.name);
        const whatsappUrl = `https://api.whatsapp.com/send?text=${encodeURIComponent(message)}`;
        window.open(whatsappUrl, '_blank');
      } catch (error) {
        console.error('Error al compartir por WhatsApp:', error);
        alert('Error al compartir el men√∫. Por favor, intenta reducir el tama√±o de las im√°genes o eliminar algunos √≠tems.');
      }
    };
    const copyLink = async () => {
      try {
        // No modificar los items antes de enviarlos, para mantener las im√°genes
        const itemsToShare = [...menuItems.value]; // Copia simple para no afectar el estado original

        const menuId = await saveMenu(itemsToShare);
        const url = `${window.location.origin}/menu/${menuId}`;
        shareLink.value = url;
        navigator.clipboard.writeText(url).then(() => {
          showCopiedMessage.value = true;
          setTimeout(() => {
            showCopiedMessage.value = false;
          }, 3000);
        }).catch(err => {
          console.error('Error al copiar:', err);
          alert('No se pudo copiar el enlace. Por favor, int√©ntalo de nuevo.');
        });
      } catch (error) {
        console.error('Error al generar enlace para compartir:', error);
        alert('Error al generar el enlace. Por favor, intenta reducir el tama√±o de las im√°genes o eliminar algunos √≠tems.');
      }
    };

    // Computados
    const totalSales = computed(() => {
      return soldItems.value.reduce((total, item) => {
        return total + item.quantity * item.price;
      }, 0);
    });

    // Formateo de precios
    const formatPrice = price => {
      return price.toFixed(2);
    };

    // Filtrado de platos vendidos
    const filteredSoldItems = computed(() => {
      let items = soldItems.value;

      // Si items no es un array, devolver un array vac√≠o
      if (!Array.isArray(items)) {
        console.warn('soldItems no es un array. Devolviendo array vac√≠o.');
        return [];
      }

      // Filtrar por t√©rmino de b√∫squeda
      if (searchTerm.value) {
        const term = searchTerm.value.toLowerCase();
        items = items.filter(item => {
          // Validar que item.name existe antes de usar toLowerCase()
          return item && item.name && typeof item.name === 'string' && item.name.toLowerCase().includes(term);
        });
      }

      // Filtrar por categor√≠a de forma segura
      if (categoryFilter.value) {
        if (categoryFilter.value === 'drinks') {
          items = items.filter(item => Boolean(item && item.includesDrink));
        } else if (categoryFilter.value === 'nodrinks') {
          items = items.filter(item => item && item.includesDrink === false);
        }
      }

      // Ordenar seg√∫n el criterio seleccionado
      if (sortBy.value) {
        items = [...items]; // Crear una copia para no mutar el original

        switch (sortBy.value) {
          case 'name':
            items.sort((a, b) => {
              // Validar que a.name y b.name existen
              if (!a || !a.name) return 1;
              if (!b || !b.name) return -1;
              return a.name.localeCompare(b.name);
            });
            break;
          case 'quantity-desc':
            items.sort((a, b) => {
              // Validar que a.quantity y b.quantity son n√∫meros
              const qtyA = a && typeof a.quantity === 'number' ? a.quantity : 0;
              const qtyB = b && typeof b.quantity === 'number' ? b.quantity : 0;
              return qtyB - qtyA;
            });
            break;
          case 'quantity-asc':
            items.sort((a, b) => {
              const qtyA = a && typeof a.quantity === 'number' ? a.quantity : 0;
              const qtyB = b && typeof b.quantity === 'number' ? b.quantity : 0;
              return qtyA - qtyB;
            });
            break;
          case 'price-desc':
            items.sort((a, b) => {
              const priceA = a && typeof a.price === 'number' ? a.price : 0;
              const priceB = b && typeof b.price === 'number' ? b.price : 0;
              return priceB - priceA;
            });
            break;
          case 'price-asc':
            items.sort((a, b) => {
              const priceA = a && typeof a.price === 'number' ? a.price : 0;
              const priceB = b && typeof b.price === 'number' ? b.price : 0;
              return priceA - priceB;
            });
            break;
          default:
            break;
        }
      }
      return items;
    });

    // Exportar datos
    const exportToExcel = () => {
      // Crear un array con los datos a exportar
      const data = [['Nombre del Plato', 'Categor√≠a', 'Cantidad Vendida', 'Precio por Unidad', 'Subtotal'], ...filteredSoldItems.value.map(item => [item.name, item.includesDrink ? 'Con refresco' : 'Sin refresco', item.quantity, `$${formatPrice(item.price)}`, `$${formatPrice(item.quantity * item.price)}`]), ['', '', '', 'Total', `$${formatPrice(totalSales.value)}`]];

      // Crear un libro de Excel y una hoja de c√°lculo
      const ws = XLSX.utils.aoa_to_sheet(data);
      const wb = XLSX.utils.book_new();
      XLSX.utils.book_append_sheet(wb, ws, 'Ventas');

      // Generar el archivo Excel y descargarlo
      const excelBuffer = XLSX.write(wb, {
        bookType: 'xlsx',
        type: 'array'
      });
      const blob = new Blob([excelBuffer], {
        type: 'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet'
      });
      saveAs(blob, `ventas_${new Date().toLocaleDateString()}.xlsx`);
    };
    const exportToPDF = () => {
      // Crear un nuevo documento PDF
      const doc = new jsPDF();

      // A√±adir t√≠tulo
      doc.setFontSize(18);
      doc.text('Reporte de Ventas', 14, 22);
      doc.setFontSize(11);
      doc.text(`Fecha: ${new Date().toLocaleDateString()}`, 14, 32);

      // Crear la tabla con los datos
      const tableData = filteredSoldItems.value.map(item => [item.name, item.includesDrink ? 'Con refresco' : 'Sin refresco', item.quantity, `$${formatPrice(item.price)}`, `$${formatPrice(item.quantity * item.price)}`]);

      // A√±adir la tabla
      doc.autoTable({
        head: [['Nombre del Plato', 'Categor√≠a', 'Cantidad Vendida', 'Precio por Unidad', 'Subtotal']],
        body: tableData,
        foot: [['', '', '', 'Total', `$${formatPrice(totalSales.value)}`]],
        startY: 40,
        theme: 'striped',
        headStyles: {
          fillColor: [76, 175, 80]
        },
        footStyles: {
          fillColor: [240, 240, 240],
          textColor: [0, 0, 0],
          fontStyle: 'bold'
        }
      });

      // Guardar el PDF de exportaci√≥n
      doc.save(`ventas_${new Date().toLocaleDateString()}.pdf`);
    };
    return {
      // Negocio
      businessInfo,
      showBusinessForm,
      toggleBusinessForm,
      onLogoChange,
      saveBusinessInfo,
      clearBusinessForm,
      // Men√∫
      newItem,
      menuItems,
      showMenuForm,
      isEditing,
      toggleMenuForm,
      onImageChange,
      saveMenuItem,
      editMenuItem,
      deleteMenuItem,
      sellMenuItem,
      clearMenuForm,
      // Ventas
      soldItems,
      totalSales,
      searchTerm,
      sortBy,
      categoryFilter,
      filteredSoldItems,
      formatPrice,
      selectedChartType,
      // M√©todos de exportaci√≥n
      exportToExcel,
      exportToPDF,
      // Compartir
      shareLink,
      showCopiedMessage,
      shareByWhatsApp,
      copyLink,
      // Estado de migraci√≥n
      migrationStatus,
      // Funci√≥n para eliminar duplicados
      removeDuplicates,
      // Errores del gr√°fico
      chartError,
      handleChartError
    };
  }
};
