/**
 * Crea un nuevo usuario
 * @param {Object} userData Datos del usuario a crear
 * @returns {Promise<Object>} Usuario creado
 */
export async function createUser(userData) {
  try {
    console.log("adminService.createUser - Iniciando creación con datos:", userData);

    // En desarrollo, simular la creación
    if (process.env.NODE_ENV === 'development' && false) { 
      // Simulando tiempo de respuesta del servidor
      await new Promise(resolve => setTimeout(resolve, 500));
      // Comprobar si ya existe un usuario con el mismo email
      const existingUser = localUsers.find(u => u.email && u.email.toLowerCase() === userData.email.toLowerCase());
      if (existingUser) {
        return {
          success: false,
          error: 'Ya existe un usuario con este correo electrónico.',
          message: 'El correo electrónico ya está registrado'
        };
      }

      // Crear un nuevo usuario con formato completo
      const newUser = {
        id: localUsers.length > 0 ? Math.max(...localUsers.map(u => u.id)) + 1 : 1,
        nombre: userData.nombre || 'Usuario sin nombre',
        email: userData.email || `usuario${Date.now()}@ejemplo.com`,
        telefono: userData.telefono || '',
        roles: Array.isArray(userData.roles) ? [...userData.roles] : ['Empleado'],
        estado: userData.estado || 'activo',
        fecha_creacion: new Date().toISOString()
      };
      console.log("Usuario creado para añadir a la lista local:", newUser);

      // Añadir a la lista local de usuarios
      localUsers.push(newUser);

      console.log("Lista actualizada de usuarios:", localUsers.length);

      return {
        success: true,
        data: newUser,
        message: 'Usuario creado con éxito'
      };
    }

    // Siempre usar la API real
    console.log("Llamando a API real para crear usuario:", userData);

    // Asegurarnos de que los roles sean un array
    if (userData.roles && !Array.isArray(userData.roles)) {
      userData.roles = [userData.roles];
    }

    // Obtener el token actual del localStorage
    const token = localStorage.getItem('token');
    console.log("Token de autenticación disponible:", !!token);

    // Verificar si hay un token disponible
    if (!token) {
      console.error("No hay token de autenticación disponible");
      return {
        success: false,
        message: 'No hay sesión activa. Por favor, inicie sesión nuevamente.'
      };
    }

    // Llamar al endpoint correcto para crear usuarios con el token en las cabeceras
    const response = await apiService.default.post('/admin/users', userData);
    console.log("Respuesta de la API:", response);
    return response;
  } catch (error) {
    console.error('Error en adminService.createUser:', error);
    throw error;
  }
}

/**
 * Actualiza un usuario existente
 * @param {String|Number} id ID del usuario
 * @param {Object} userData Datos actualizados del usuario
 * @returns {Promise<Object>} Usuario actualizado
 */
export async function updateUser(id, userData) {
  try {
    console.log(`adminService.updateUser - Actualizando usuario con ID ${id}:`, userData);

    // En desarrollo, simular la actualización
    if (process.env.NODE_ENV === 'development' && false) { // Desactivamos la simulación local
      // Simulando tiempo de respuesta del servidor
      await new Promise(resolve => setTimeout(resolve, 500));

      // Buscar el usuario en localUsers
      const userIndex = localUsers.findIndex(user => user.id == id);
      if (userIndex === -1) {
        return {
          success: false,
          message: `Usuario con ID ${id} no encontrado`
        };
      }

      // Actualizar en la lista local
      const updatedUser = {
        ...localUsers[userIndex],
        ...userData
      };
      localUsers[userIndex] = updatedUser;

      console.log('Usuario actualizado:', updatedUser);
      return {
        success: true,
        data: updatedUser,
        message: 'Usuario actualizado con éxito'
      };
    }

    // Obtener el token actual del localStorage
    const token = localStorage.getItem('token');
    console.log("Token de autenticación disponible:", !!token);

    // Verificar si hay un token disponible
    if (!token) {
      console.error("No hay token de autenticación disponible");
      return {
        success: false,
        message: 'No hay sesión activa. Por favor, inicie sesión nuevamente.'
      };
    }

    // Asegurarnos de que los roles sean un array
    if (userData.roles && !Array.isArray(userData.roles)) {
      userData.roles = [userData.roles];
    }

    // Convertir el ID a string para asegurarnos de que se pasa correctamente
    const userId = String(id);
    console.log(`Llamando a API para actualizar usuario con ID ${userId}`);

    // Llamar a la API real
    const response = await apiService.default.put(`/admin/users/${userId}`, userData);
    console.log("Respuesta de la API de actualización:", response);
    return response;
  } catch (error) {
    console.error(`Error al actualizar usuario con ID ${id}:`, error);
    throw error;
  }
}

/**
