/**
 * Elimina un usuario
 * @param {String|Number} id ID del usuario a eliminar
 * @returns {Promise<Object>} Resultado de la operación
 */
export async function deleteUser(id) {
  try {
    console.log(`adminService.deleteUser: Eliminando usuario con ID ${id}`);

    // En desarrollo, simular la eliminación
    if (process.env.NODE_ENV === 'development' && false) { // Desactivamos la simulación local
      // Simulando tiempo de respuesta del servidor
      await new Promise(resolve => setTimeout(resolve, 500));

      // Verificar que el ID existe
      const index = localUsers.findIndex(user => user.id == id);
      if (index === -1) {
        return {
          success: false,
          message: `Usuario con ID ${id} no encontrado`
        };
      }

      // Eliminar el usuario
      const deletedUser = localUsers.splice(index, 1)[0];
      console.log(`Usuario eliminado: ${deletedUser.nombre} (ID: ${deletedUser.id})`);
      return {
        success: true,
        message: 'Usuario eliminado exitosamente'
      };
    }

    // Obtener el token actual del localStorage
    const token = localStorage.getItem('token');
    console.log("Token de autenticación disponible:", !!token);

    // Verificar si hay un token disponible
    if (!token) {
      console.error("No hay token de autenticación disponible");
      return {
        success: false,
        message: 'No hay sesión activa. Por favor, inicie sesión nuevamente.'
      };
    }

    // Convertir el ID a string para asegurarnos de que se pasa correctamente
    const userId = String(id);
    console.log(`Llamando a API para eliminar usuario con ID ${userId}`);

    // Llamar a la API real - usar delete en lugar de del ya que así está exportado en apiService
    const response = await apiService.default.delete(`/admin/users/${userId}`);
    console.log("Respuesta de la API de eliminación:", response);
    return response;
