import "core-js/modules/es.iterator.constructor.js";
import "core-js/modules/es.iterator.filter.js";
import { ref, onMounted, computed } from 'vue';
// Importar todas las funciones que necesitamos
import * as adminService from '../../services/adminService';
import { alert, confirm } from '../../services/dialogService';
import { hasRole } from '../../services/authService';
export default {
  name: 'AdminUsers',
  emits: ['user-created'],
  // Declarar los eventos que emite el componente
  setup(props, {
    emit
  }) {
    // Obtener emit desde el contexto
    const allUsers = ref([]); // Almacena todos los usuarios sin filtrar
    const users = ref([]); // Usuarios filtrados para mostrar
    const roles = ref([]);
    const loading = ref(true);
    const error = ref(null);

    // Filtros
    const searchTerm = ref('');
    const roleFilter = ref('');
    const statusFilter = ref('');

    // Formulario
    const showModal = ref(false);
    const isEditing = ref(false);
    const formData = ref({
      id: null,
      nombre: '',
      email: '',
      telefono: '',
      password: '',
      roles: [],
      estado: 'activo'
    });

    // Obtener referencias a las funciones del servicio admin
    const {
      getUsers,
      getRoles,
      createUser,
      updateUser,
      cambiarEstadoUsuario,
      deleteUser: removeUser
    } = adminService;

    // B√∫squeda con debounce
    let searchTimeout = null;

    // Obtener si el usuario actual es Administrador (no Superadministrador)
    const isOnlyAdmin = computed(() => {
      return hasRole('Administrador') && !hasRole('Superadministrador');
    });

    // Filtrar roles disponibles seg√∫n el rol del usuario actual
    const availableRoles = computed(() => {
      if (isOnlyAdmin.value) {
        // Si es solo Administrador, mostrar solo el rol de Empleado
        return roles.value.filter(role => role.nombre === 'Empleado');
      }
      // Si es Superadministrador, mostrar todos los roles
      return roles.value;
    });

    // Filtrar usuarios seg√∫n el rol del usuario actual
    const filteredUsers = computed(() => {
      if (isOnlyAdmin.value) {
        // Si es solo Administrador, ocultar usuarios con rol de Superadministrador
        return allUsers.value.filter(user => {
          // Verificar si el usuario tiene roles y si no incluye el rol de Superadministrador
          return !user.roles || !Array.isArray(user.roles) || !user.roles.includes('Superadministrador');
        });
      }
      return allUsers.value;
    });
    onMounted(async () => {
      try {
        console.log("AdminUsers componente montado - inicializando...");
        // Inicializar la lista de usuarios vac√≠a
        allUsers.value = [];
        users.value = [];
        // Cargar datos
        await Promise.all([fetchUsers(), fetchRoles()]);
        console.log("Inicializaci√≥n completa, usuarios cargados:", users.value.length);
      } catch (initError) {
        console.error("Error durante la inicializaci√≥n:", initError);
        error.value = `Error durante la inicializaci√≥n: ${initError.message}`;
      }
    });

    // Obtener usuarios con filtros aplicados
    const fetchUsers = async () => {
      try {
        loading.value = true;
        error.value = null;
        const options = {
          searchTerm: searchTerm.value,
          role: roleFilter.value,
          status: statusFilter.value
        };
        console.log("Solicitando usuarios con opciones:", options);
        const response = await getUsers(options);
        if (response && response.success) {
          // Asegurarnos que la respuesta contiene datos
          if (Array.isArray(response.data)) {
            console.log(`Actualizando lista de usuarios: recibidos ${response.data.length} usuarios`);
            allUsers.value = response.data;

            // Aplicar filtro para ocultar superadministradores si el usuario actual es solo Administrador
            users.value = filteredUsers.value;
          } else {
            console.warn("La respuesta de getUsers no contiene un array:", response.data);
          }
        } else {
          console.warn("Respuesta no exitosa de la API:", response);
          throw new Error(response?.message || 'Error al cargar usuarios');
        }
      } catch (err) {
        console.error('Error al cargar usuarios:', err);
        error.value = `Error al cargar usuarios: ${err.message || 'Error desconocido'}`;
      } finally {
        loading.value = false;
      }
    };

    // Obtener roles disponibles
    const fetchRoles = async () => {
      try {
        const response = await getRoles();
        if (response && response.success) {
          roles.value = Array.isArray(response.data) ? response.data : [];
        } else {
          console.warn("Error al cargar roles:", response?.message);
        }
      } catch (err) {
        console.error('Error al cargar roles:', err);
      }
    };

    // Manejar cambios en el campo de b√∫squeda con debounce
    const handleSearchInput = () => {
      clearTimeout(searchTimeout);
      searchTimeout = setTimeout(() => {
        fetchUsers();
      }, 300);
    };

    // Resetear filtros
    const resetFilters = () => {
      searchTerm.value = '';
      roleFilter.value = '';
      statusFilter.value = '';
      fetchUsers();
    };

    // Mostrar formulario de creaci√≥n
    const showCreateForm = () => {
      isEditing.value = false;
      formData.value = {
        id: null,
        nombre: '',
        email: '',
        telefono: '',
        password: '',
        roles: ['Empleado'],
        // Rol por defecto
        estado: 'activo'
      };
      showModal.value = true;
    };

    // Editar usuario
    const editUser = user => {
      isEditing.value = true;
      formData.value = {
        id: user.id,
        nombre: user.nombre,
        email: user.email,
        telefono: user.telefono || '',
        password: '',
        // Dejamos vac√≠o para mantener la contrase√±a actual
        roles: [...user.roles],
        estado: user.estado
      };
      showModal.value = true;
    };

    // Guardar usuario (crear o actualizar)
    const saveUser = async () => {
      try {
        loading.value = true;

        // Validar que al menos se seleccion√≥ un rol
        if (!formData.value.roles.length) {
          await alert('Debe seleccionar al menos un rol para el usuario');
          loading.value = false;
          return;
        }

        // Validar que un Administrador no asigne roles superiores
        if (isOnlyAdmin.value) {
          // Filtrar los roles para mantener solo el de Empleado
          formData.value.roles = formData.value.roles.filter(role => role === 'Empleado');

          // Si despu√©s del filtro no queda ning√∫n rol, a√±adimos Empleado
          if (formData.value.roles.length === 0) {
            formData.value.roles = ['Empleado'];
          }
        }
        if (isEditing.value) {
          // Filtrar para enviar solo los campos necesarios
          const userData = {
            nombre: formData.value.nombre,
            telefono: formData.value.telefono,
            roles: formData.value.roles,
            estado: formData.value.estado
          };

          // Si se proporciona una contrase√±a, incluirla
          if (formData.value.password) {
            userData.password = formData.value.password;
          }
          const response = await updateUser(formData.value.id, userData);
          if (response && response.success) {
            await alert(response.message || 'Usuario actualizado exitosamente');
            // Refrescar lista de usuarios con los nuevos datos
            await fetchUsers();
          } else {
            throw new Error(response?.message || 'Error al actualizar usuario');
          }
        } else {
          // Crear nuevo usuario
          const userData = {
            nombre: formData.value.nombre,
            email: formData.value.email,
            password: formData.value.password,
            telefono: formData.value.telefono,
            roles: formData.value.roles
          };
          console.log("Llamando a createUser con:", userData);
          const response = await createUser(userData);
          if (response && response.success) {
            await alert(response.message || 'Usuario creado exitosamente');

            // A√±adir directamente el usuario creado a la lista local
            if (response.data) {
              users.value = [response.data, ...users.value];
            }

            // Emitir evento para refrescar estad√≠sticas en el dashboard
            emit('user-created');

            // Tambi√©n hacer un fetchUsers completo por si acaso
            await fetchUsers();
          } else {
            throw new Error(response?.message || 'Error al crear usuario');
          }
        }
        closeModal();
      } catch (err) {
        console.error('Error al guardar el usuario:', err);
        await alert(`Error al guardar el usuario: ${err.message || 'Error desconocido'}`);
      } finally {
        loading.value = false;
      }
    };

    // Activar usuario
    const activateUser = async user => {
      try {
        const confirmed = await confirm(`¬øEst√°s seguro de que deseas activar el usuario ${user.nombre}?`);
        if (confirmed) {
          loading.value = true;
          console.log(`Activando usuario con ID: ${user.id}`);
          const response = await cambiarEstadoUsuario(user.id, 'activo');
          if (response && response.success) {
            await alert(response.message || 'Usuario activado exitosamente');

            // Actualizar estado del usuario localmente en la lista
            const index = users.value.findIndex(u => u.id === user.id);
            if (index !== -1) {
              users.value[index].estado = 'activo';
            }
          } else {
            throw new Error(response?.message || 'Error al activar usuario');
          }
        }
      } catch (err) {
        console.error('Error al activar usuario:', err);
        await alert(`Error al activar usuario: ${err.message || 'Error desconocido'}`);
      } finally {
        loading.value = false;
      }
    };

    // Desactivar usuario
    const deactivateUser = async user => {
      try {
        const confirmed = await confirm(`¬øEst√°s seguro de que deseas desactivar el usuario ${user.nombre}? No podr√° acceder al sistema mientras est√© inactivo.`, {
          title: 'Confirmar desactivaci√≥n'
        });
        if (confirmed) {
          loading.value = true;
          console.log(`Desactivando usuario con ID: ${user.id}`);
          const response = await cambiarEstadoUsuario(user.id, 'inactivo');
          if (response && response.success) {
            await alert(response.message || 'Usuario desactivado exitosamente');

            // Actualizar estado del usuario localmente en la lista
            const index = users.value.findIndex(u => u.id === user.id);
            if (index !== -1) {
              users.value[index].estado = 'inactivo';
            }
          } else {
            throw new Error(response?.message || 'Error al desactivar usuario');
          }
        }
      } catch (err) {
        console.error('Error al desactivar usuario:', err);
        await alert(`Error al desactivar usuario: ${err.message || 'Error desconocido'}`);
      } finally {
        loading.value = false;
      }
    };

    // Eliminar usuario
    const deleteUser = async user => {
      try {
        const confirmed = await confirm(`¬øEst√°s seguro de que deseas eliminar permanentemente el usuario ${user.nombre}? Esta acci√≥n no se puede deshacer.`, {
          title: 'Confirmar eliminaci√≥n',
          confirmText: 'Eliminar',
          cancelText: 'Cancelar'
        });
        if (confirmed) {
          loading.value = true;
          console.log(`Eliminando usuario con ID: ${user.id}`);
          const response = await removeUser(user.id);
          if (response && response.success) {
            await alert(response.message || 'Usuario eliminado exitosamente');

            // Eliminar usuario de la lista local
            users.value = users.value.filter(u => u.id !== user.id);
          } else {
            throw new Error(response?.message || 'Error al eliminar usuario');
          }
        }
      } catch (err) {
        console.error('Error al eliminar usuario:', err);
        await alert(`Error al eliminar usuario: ${err.message || 'Error desconocido'}`);
      } finally {
        loading.value = false;
      }
    };

    // Cerrar modal
    const closeModal = () => {
      showModal.value = false;
    };

    // Formatear fecha
    const formatDate = dateStr => {
      if (!dateStr) return 'N/A';
      try {
        const date = new Date(dateStr);
        if (isNaN(date.getTime())) return 'Fecha inv√°lida';
        return new Intl.DateTimeFormat('es-ES', {
          day: '2-digit',
          month: '2-digit',
          year: 'numeric',
          hour: '2-digit',
          minute: '2-digit'
        }).format(date);
      } catch (e) {
        return 'Fecha inv√°lida';
      }
    };
    return {
      users,
      roles,
      loading,
      error,
      searchTerm,
      roleFilter,
      statusFilter,
      showModal,
      isEditing,
      formData,
      fetchUsers,
      handleSearchInput,
      resetFilters,
      showCreateForm,
      editUser,
      saveUser,
      activateUser,
      deactivateUser,
      deleteUser,
      closeModal,
      formatDate,
      availableRoles,
      isOnlyAdmin,
      filteredUsers
    };
  }
};
