      qrImage: '',
      nequiNumber: '',
      nequiImage: ''
    });
    const showPaymentForm = ref(false);

    // Estado para gestión de inventario
    const inventoryItems = ref([]);
    const inventoryFilter = ref('all');
    const showInventorySection = ref(false);

    // Propiedad computada para filtrar items de inventario
    const filteredInventoryItems = computed(() => {
      if (inventoryFilter.value === 'all') {
        return inventoryItems.value;
      } else if (inventoryFilter.value === 'available') {
        return inventoryItems.value.filter(item => item.availableQuantity > 0);
      } else {
        return inventoryItems.value.filter(item => item.availableQuantity <= 0);
      }
    });

    // Estado para categorías
    const categories = ref([]);
