    const newCategory = ref({
      name: '',
      description: ''
    });
    const showAddCategoryForm = ref(false);

    // Estado para notificaciones
    const notification = ref({
      show: false,
      message: '',
      type: 'success',
      timeout: null
    });

    // Estado para indicadores de carga
    const loading = ref({
      businessInfo: false,
      paymentInfo: false,
      inventory: false,
      categories: false
    });

    // Cargar datos al montar el componente
    onMounted(async () => {
      try {
        // Cargar información del negocio desde IndexedDB
        const {
          getBusinessInfo
