            // Filtrar solo los elementos que son platos (no categorías)
            inventoryItems.value = items.filter(item => !item.isCategory);
          }

          // Extraer categorías de los items (en este caso, asumimos que las categorías están marcadas con isCategory=true)
          // o podemos usar una lista predefinida si no hay categorías en la base de datos
          const cats = items ? items.filter(item => item.isCategory) : [];
          if (cats && cats.length > 0) {
            categories.value = cats;
          } else {
            // Categorías predefinidas si no hay ninguna en la base de datos
            categories.value = [{
