    // Función para sincronizar con el backend con manejo adecuado de conexiones
    const syncWithBackend = async (data, type) => {
      try {
        const {
          syncBusinessInfoWithBackend
        } = await import('../services/menuService');

        // Añadir metadatos para ayudar al backend a gestionar las conexiones eficientemente
        const syncData = {
          ...data,
          syncType: type,
          timestamp: new Date().toISOString(),
          // Añadir un identificador único para cada solicitud
          requestId: `sync_${type}_${Date.now()}_${Math.random().toString(36).substring(2, 9)}`
        };

        // Realizar la sincronización
        const result = await syncBusinessInfoWithBackend(syncData);
        if (result && result.success) {
          console.log(`Sincronización de ${type} completada con éxito`);
          return {
            success: true
          };
        } else {
          console.error(`Error en sincronización de ${type}:`, result);
          return {
            success: false,
            message: result?.message || `Error al sincronizar ${type}`
          };
