        if (notification.value.timeout) {
          clearTimeout(notification.value.timeout);
        }

        // Validar campos obligatorios
        if (!businessInfo.value.name || businessInfo.value.name.trim() === '') {
          notification.value = {
            show: true,
            message: 'El nombre del negocio es obligatorio',
            type: 'error',
            timeout: setTimeout(() => {
              notification.value.show = false;
            }, 3000)
          };
          return;
        }

        // Mostrar indicador de carga
        loading.value.businessInfo = true;
        await saveBusinessInfoToDB(businessInfo.value);

        // Sincronizar con el backend
        try {
          const syncResult = await syncWithBackend(businessInfo.value, 'business_info');
