        // Cambiar la disponibilidad del item
        const updatedItem = {
          ...item,
          availableQuantity: item.availableQuantity > 0 ? 0 : 10,
          updatedAt: new Date().toISOString()
        };

        // Actualizar el item en el array local
        const index = inventoryItems.value.findIndex(i => i.id === item.id);
        if (index !== -1) {
          inventoryItems.value[index] = updatedItem;
        }

        // Guardar en IndexedDB
        const {
          saveMenuItem
