            };
          }
        } catch (syncError) {
          console.error('Error al sincronizar disponibilidad del item:', syncError);
          notification.value = {
            show: true,
            message: 'Disponibilidad actualizada localmente, pero hubo un error al sincronizar con el servidor',
            type: 'warning',
            timeout: setTimeout(() => {
              notification.value.show = false;
            }, 3000)
          };
        }
        notification.value = {
          show: true,
          message: `Item ${updatedItem.availableQuantity > 0 ? 'disponible' : 'no disponible'} actualizado con éxito!`,
