          message: 'Error al actualizar la disponibilidad',
          type: 'error',
          timeout: setTimeout(() => {
            notification.value.show = false;
          }, 3000)
        };
      }
    };

    // Métodos para categorías
    const toggleCategoriesSection = () => {
      showCategoriesSection.value = !showCategoriesSection.value;
    };
    const editCategory = category => {
      console.log('Editar categoría:', category);
    };
    const deleteCategory = async category => {
      try {
        // Confirmar la eliminación (en una implementación real, esto sería un modal de confirmación)
        const confirmDelete = confirm(`¿Está seguro de eliminar la categoría "${category.name}"?`);
        if (!confirmDelete) return;
        categories.value = categories.value.filter(c => c.id !== category.id);

        // Eliminar de IndexedDB
        try {
          const {
            deleteMenuItemFromDB
          } = await import('../services/storageService');
          await deleteMenuItemFromDB(category.id);

          // Sincronizar con el backend
          try {
            const syncResult = await syncWithBackend({
              categoryId: category.id
            }, 'category_delete');
            if (!syncResult.success) {
              console.error('Error al sincronizar eliminación de categoría:', syncResult.message);
              notification.value = {
                show: true,
                message: 'Categoría eliminada localmente, pero hubo un error al sincronizar con el servidor',
                type: 'warning',
                timeout: setTimeout(() => {
                  notification.value.show = false;
                }, 3000)
              };
            }
          } catch (syncError) {
            console.error('Error al sincronizar eliminación de categoría:', syncError);
            notification.value = {
              show: true,
              message: 'Categoría eliminada localmente, pero hubo un error al sincronizar con el servidor',
              type: 'warning',
              timeout: setTimeout(() => {
                notification.value.show = false;
              }, 3000)
            };
          }
        } catch (error) {
          console.error('Error al eliminar categoría:', error);
          notification.value = {
            show: true,
            message: 'Error al eliminar la categoría',
            type: 'error',
            timeout: setTimeout(() => {
              notification.value.show = false;
            }, 3000)
          };
        }
        notification.value = {
          show: true,
          message: 'Categoría eliminada con éxito!',
