        notification.value = {
          show: true,
          message: 'Error al eliminar la categoría',
          type: 'error',
          timeout: setTimeout(() => {
            notification.value.show = false;
          }, 3000)
        };
      }
    };
    const saveNewCategory = async () => {
      try {
        if (notification.value.timeout) {
          clearTimeout(notification.value.timeout);
        }

        // Validar que la categoría tenga un nombre
        if (!newCategory.value.name.trim()) {
          notification.value = {
            show: true,
            message: 'El nombre de la categoría es obligatorio',
            type: 'error',
            timeout: setTimeout(() => {
              notification.value.show = false;
            }, 3000)
          };
          return;
        }

        // Crear un ID único para la nueva categoría
        const newCategoryWithId = {
          ...newCategory.value,
          id: 'category_' + Date.now(),
          createdAt: new Date().toISOString()
        };

        // Añadir la categoría al array local
        categories.value.push(newCategoryWithId);

        // Guardar en IndexedDB
        const {
          saveMenuItem
        } = await import('../services/storageService');
        await saveMenuItem({
          ...newCategoryWithId,
          isCategory: true
        });

        // Sincronizar con el backend
        try {
          const syncResult = await syncWithBackend(newCategoryWithId, 'category_update');
          if (!syncResult.success) {
            console.error('Error al sincronizar categoría:', syncResult.message);
            notification.value = {
              show: true,
              message: 'Categoría guardada localmente, pero hubo un error al sincronizar con el servidor',
              type: 'warning',
              timeout: setTimeout(() => {
                notification.value.show = false;
              }, 3000)
            };
          }
        } catch (syncError) {
          console.error('Error al sincronizar categoría:', syncError);
          notification.value = {
            show: true,
            message: 'Categoría guardada localmente, pero hubo un error al sincronizar con el servidor',
            type: 'warning',
            timeout: setTimeout(() => {
              notification.value.show = false;
            }, 3000)
          };
