        // Limpiar el formulario
        clearCategoryForm();

        // Mostrar notificación de éxito
        notification.value = {
          show: true,
          message: 'Categoría guardada con éxito!',
          type: 'success',
          timeout: setTimeout(() => {
            notification.value.show = false;
          }, 3000)
        };
