        console.error('Error al guardar categoría:', error);
        notification.value = {
          show: true,
          message: 'Error al guardar categoría',
          type: 'error',
          timeout: setTimeout(() => {
            notification.value.show = false;
          }, 3000)
        };
      }
    };
    const clearCategoryForm = () => {
      newCategory.value = {
        name: '',
        description: ''
      };
    };
    return {
      businessInfo,
      showBusinessForm,
      toggleBusinessForm,
      onLogoChange,
      saveBusinessInfo,
      clearBusinessForm,
      paymentInfo,
      showPaymentForm,
      togglePaymentForm,
      onQRImageChange,
      onNequiImageChange,
      savePaymentInfo,
      clearPaymentForm,
      inventoryItems,
      inventoryFilter,
      showInventorySection,
      toggleInventorySection,
      editInventoryItem,
      toggleItemAvailability,
      filteredInventoryItems,
      categories,
      showCategoriesSection,
      toggleCategoriesSection,
      editCategory,
      deleteCategory,
      newCategory,
      showAddCategoryForm,
      saveNewCategory,
      clearCategoryForm,
      notification,
      loading
    };
