        email: 'juan@ejemplo.com',
        rol: 'Administrador',
        estado: 'activo',
        ultimo_acceso: new Date(Date.now() - 2 * 24 * 60 * 60 * 1000).toISOString()
      }, {
        id: 3,
        nombre: 'María García',
        email: 'maria@ejemplo.com',
        rol: 'Editor',
        estado: 'inactivo',
        ultimo_acceso: new Date(Date.now() - 10 * 24 * 60 * 60 * 1000).toISOString()
      }];

      // Añadir más usuarios simulados
      for (let i = 4; i <= 45; i++) {
        mockUsers.push({
          id: i,
          nombre: `Usuario ${i}`,
          email: `usuario${i}@ejemplo.com`,
          rol: i % 5 === 0 ? 'Administrador' : i % 3 === 0 ? 'Editor' : 'Usuario',
          estado: i % 4 === 0 ? 'inactivo' : 'activo',
          ultimo_acceso: new Date(Date.now() - i * 2 * 24 * 60 * 60 * 1000).toISOString()
        });
      }
      localUsers = mockUsers;
      return {
        success: true,
        data: mockUsers
      };
    }

    // En producción, intentar obtener los usuarios desde la API
    const response = await apiService.get('/admin/users', options);
    if (response && response.success) {
      localUsers = response.data || [];
      return {
        success: true,
        data: localUsers
      };
    } else {
      throw new Error(response.error || 'Error al obtener usuarios');
