import { createRouter, createWebHistory } from 'vue-router'
import HomeView from '../views/HomeView.vue'
import { isAuthenticated, hasRole } from '../services/authService'
import { alert } from '../services/dialogService'

const routes = [
  {
    path: '/',
    name: 'home',
    component: HomeView
  },
  {
    path: '/about',
    name: 'about',
    component: () => import(/* webpackChunkName: "about" */ '../views/AboutView.vue')
  },
  {
    path: '/login',
    name: 'login',
    component: () => import(/* webpackChunkName: "login" */ '../views/LoginView.vue')
  },
  {
    path: '/register',
    name: 'register',
    component: () => import(/* webpackChunkName: "register" */ '../views/RegisterView.vue')
  },
  {
    path: '/menu',
    name: 'menu',
    component: () => import(/* webpackChunkName: "menu" */ '../components/MenuComponent.vue'),
    meta: { requiresAuth: true }
  },
  {
    path: '/menu/:id',
    name: 'shared-menu',
    component: () => import(/* webpackChunkName: "shared-menu" */ '../views/SharedMenuView.vue'),
    props: true
  },
  {
    path: '/superadmin',
    name: 'superadmin',
    component: () => import(/* webpackChunkName: "superadmin" */ '../views/SuperAdminView.vue'),
    meta: { requiresAuth: true, requiresSuperAdmin: true }
  }
]

const router = createRouter({
  history: createWebHistory(process.env.BASE_URL),
  routes
})

// Navegación con protección de rutas
router.beforeEach(async (to, from, next) => {
  // Verificar si la ruta requiere autenticación
  if (to.matched.some(record => record.meta.requiresAuth)) {
    if (!isAuthenticated()) {
      // Si no está autenticado, redirigir a login
      await alert('Debe iniciar sesión para acceder a esta página', {
        title: 'Acceso Restringido'
      });
      next({
        path: '/login',
        query: { redirect: to.fullPath }
      });
      return;
    }
    
    // Si requiere rol de superadmin
    if (to.matched.some(record => record.meta.requiresSuperAdmin)) {
      if (!hasRole('Superadministrador')) {
        await alert('No tiene permisos para acceder a esta sección', {
          title: 'Acceso Denegado'
        });
        next({ path: '/menu' });
        return;
      }
    }
  }
  
  next();
})

export default router
