        console.log('Items especiales:', specialMenuItems.value.length);
        const menuId = await saveMenu(itemsToShare);
        const url = `${window.location.origin}/menu/${menuId}`;
        shareLink.value = url;
        const message = formatShareLinkMessage(url, businessInfo.value.name);
        const whatsappUrl = `https://api.whatsapp.com/send?text=${encodeURIComponent(message)}`;
        window.open(whatsappUrl, '_blank');
      } catch (error) {
        console.error('Error al compartir por WhatsApp:', error);
        await alert(`Error al compartir el menú: ${error.message || 'Error desconocido'}. Por favor, intenta reducir el tamaño de las imágenes o eliminar algunos ítems.`);
      }
    };
    const copyLink = async () => {
      try {
        const loadingMessage = 'Procesando imágenes para compartir...';
        showCopiedMessage.value = true;
        const totalItems = menuItems.value.length + specialMenuItems.value.length;
        if (totalItems > 30) {
          await alert('El menú tiene demasiados elementos. Por favor, seleccione menos platos para compartir.');
          showCopiedMessage.value = false;
          return;
        }

        // Combinar platos regulares y especiales
        const itemsToShare = [...menuItems.value.map(item => ({
          ...item,
          isSpecial: false
        })), ...specialMenuItems.value.map(item => ({
          ...item,
          isSpecial: true
        }))];
        const menuId = await saveMenu(itemsToShare);
        const url = `${window.location.origin}/menu/${menuId}`;
        shareLink.value = url;
        try {
          await navigator.clipboard.writeText(url);
          showCopiedMessage.value = true;
          setTimeout(() => {
            showCopiedMessage.value = false;
          }, 3000);
        } catch (clipboardError) {
          console.error('Error al copiar al portapapeles:', clipboardError);
          await alert('No se pudo copiar automáticamente. Por favor, copia manualmente este enlace: ' + url);
        }
      } catch (error) {
        console.error('Error al generar enlace para compartir:', error);
        showCopiedMessage.value = false;
        await alert(`Error al generar el enlace: ${error.message || 'Error desconocido'}. Por favor, intenta reducir el tamaño de las imágenes o eliminar algunos ítems.`);
      }
    };

    // Computados
    const totalSales = computed(() => {
      return soldItems.value.reduce((total, item) => {
        return total + item.quantity * item.price;
      }, 0);
    });
    const formatPrice = price => {
      if (isNaN(Number(price))) return '0,00';
      const fixed = Number(price).toFixed(2);
      const [intPart, decPart] = fixed.split('.');
      const formattedIntPart = intPart.replace(/\B(?=(\d{3})+(?!\d))/g, '.');
      return `${formattedIntPart},${decPart}`;
    };
    const formatDate = dateStr => {
      if (!dateStr) return 'N/A';
      const date = new Date(dateStr);
      if (isNaN(date.getTime())) return 'Fecha inválida';
      return new Intl.DateTimeFormat('es-ES', {
        day: '2-digit',
        month: '2-digit',
        year: 'numeric',
        hour: '2-digit',
        minute: '2-digit'
      }).format(date);
    };
    const filteredSoldItems = computed(() => {
      let items = soldItems.value;
      if (!Array.isArray(items)) {
        console.warn('soldItems no es un array. Devolviendo array vacío.');
        return [];
      }
      if (searchTerm.value) {
        const term = searchTerm.value.toLowerCase();
        items = items.filter(item => {
          return item && item.name && typeof item.name === 'string' && item.name.toLowerCase().includes(term);
        });
      }
      if (categoryFilter.value) {
        if (categoryFilter.value === 'drinks') {
          items = items.filter(item => Boolean(item && item.includesDrink));
        } else if (categoryFilter.value === 'nodrinks') {
          items = items.filter(item => item && item.includesDrink === false);
        }
      }
      if (sortBy.value) {
        items = [...items];
        switch (sortBy.value) {
          case 'name':
            items.sort((a, b) => {
              if (!a || !a.name) return 1;
              if (!b || !b.name) return -1;
              return a.name.localeCompare(b.name);
            });
            break;
          case 'quantity-desc':
            items.sort((a, b) => {
              const qtyA = a && typeof a.quantity === 'number' ? a.quantity : 0;
              const qtyB = b && typeof b.quantity === 'number' ? b.quantity : 0;
              return qtyB - qtyA;
            });
            break;
          case 'quantity-asc':
            items.sort((a, b) => {
              const qtyA = a && typeof a.quantity === 'number' ? a.quantity : 0;
              const qtyB = b && typeof b.quantity === 'number' ? b.quantity : 0;
              return qtyA - qtyB;
            });
            break;
          case 'price-desc':
            items.sort((a, b) => {
              const priceA = a && typeof a.price === 'number' ? a.price : 0;
              const priceB = b && typeof b.price === 'number' ? b.price : 0;
              return priceB - priceA;
            });
            break;
          case 'price-asc':
            items.sort((a, b) => {
              const priceA = a && typeof a.price === 'number' ? a.price : 0;
              const priceB = b && typeof b.price === 'number' ? b.price : 0;
              return priceA - priceB;
            });
            break;
          case 'date-desc':
            items.sort((a, b) => {
              const dateA = a && a.date ? new Date(a.date) : new Date(0);
              const dateB = b && b.date ? new Date(b.date) : new Date(0);
              return dateB - dateA;
            });
            break;
          case 'date-asc':
            items.sort((a, b) => {
              const dateA = a && a.date ? new Date(a.date) : new Date(0);
              const dateB = b && b.date ? new Date(b.date) : new Date(0);
              return dateA - dateB;
            });
            break;
          default:
            break;
        }
      }
      return items;
    });
    const exportToExcel = () => {
      // Crear un array con los datos a exportar
      const data = [['Nombre del Plato', 'Categoría', 'Cantidad Vendida', 'Precio por Unidad', 'Fecha de Venta', 'Subtotal'], ...filteredSoldItems.value.map(item => [item.name, item.includesDrink ? 'Con refresco' : 'Sin refresco', item.quantity, `$${formatPrice(item.price)}`, formatDate(item.date), `$${formatPrice(item.quantity * item.price)}`]), ['', '', '', '', 'Total', `$${formatPrice(totalSales.value)}`]];

      // Crear un libro de Excel y una hoja de cálculo
      const ws = XLSX.utils.aoa_to_sheet(data);
      const wb = XLSX.utils.book_new();
      XLSX.utils.book_append_sheet(wb, ws, 'Ventas');

      // Generar el archivo Excel y descargarlo
      const excelBuffer = XLSX.write(wb, {
        bookType: 'xlsx',
        type: 'array'
      });
      const blob = new Blob([excelBuffer], {
        type: 'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet'
      });
      saveAs(blob, `ventas_${new Date().toLocaleDateString()}.xlsx`);
    };
    const exportToPDF = () => {
      // Crear un nuevo documento PDF
      const doc = new jsPDF();

      // Añadir título
      doc.setFontSize(18);
      doc.text('Reporte de Ventas', 14, 22);
      doc.setFontSize(11);
      doc.text(`Fecha: ${new Date().toLocaleDateString()}`, 14, 32);

      // Crear la tabla con los datos
      const tableData = filteredSoldItems.value.map(item => [item.name, item.includesDrink ? 'Con refresco' : 'Sin refresco', item.quantity, `$${formatPrice(item.price)}`, formatDate(item.date), `$${formatPrice(item.quantity * item.price)}`]);

      // Añadir la tabla
      doc.autoTable({
        head: [['Nombre del Plato', 'Categoría', 'Cantidad Vendida', 'Precio por Unidad', 'Fecha de Venta', 'Subtotal']],
        body: tableData,
        foot: [['', '', '', '', 'Total', `$${formatPrice(totalSales.value)}`]],
        startY: 40,
        theme: 'striped',
        headStyles: {
          fillColor: [76, 175, 80]
        },
        footStyles: {
          fillColor: [240, 240, 240],
          textColor: [0, 0, 0],
          fontStyle: 'bold'
        }
      });

      // Guardar el PDF de exportación
      doc.save(`ventas_${new Date().toLocaleDateString()}.pdf`);
    };
    const removeDuplicates = array => {
      return [...new Set(array)];
    };

    // Función para mostrar notificaciones
    function showToast(type, message) {
      toast.value = {
        show: true,
        type,
        message
      };
      setTimeout(() => {
        toast.value.show = false;
      }, 3000); // 3 segundos como acordado
    }
    const checkAvailabilityToast = async itemId => {
      try {
        // Buscar el item en ambos menús
        const item = menuItems.value.find(i => i.id === itemId) || specialMenuItems.value.find(i => i.id === itemId);
        if (item) {
          if (item.availableQuantity > 5) {
            showToast('success', `Disponible: ${item.availableQuantity} unidades`);
          } else if (item.availableQuantity > 0) {
            showToast('warning', `¡Quedan solo ${item.availableQuantity} unidades!`);
          } else {
            showToast('error', 'Producto agotado');
          }
        }
      } catch (error) {
        console.error('Error al verificar disponibilidad:', error);
      }
    };

    // Añadir propiedad isEmployee
    const isEmployee = computed(() => {
      return hasRole('Empleado') && !hasRole('Administrador') && !hasRole('Superadministrador');
    });
    return {
      businessInfo,
      showBusinessForm,
      toggleBusinessForm,
      onLogoChange,
      saveBusinessInfo,
      clearBusinessForm,
      paymentInfo,
      showPaymentForm,
      togglePaymentForm,
      onQRImageChange,
      onNequiImageChange,
      savePaymentInfo,
      clearPaymentForm,
      newItem,
      menuItems,
      showMenuForm,
      isEditing,
      toggleMenuForm,
      onImageChange,
      saveMenuItem,
      editMenuItem,
      deleteMenuItem,
      sellMenuItem,
      clearMenuForm,
      soldItems,
      totalSales,
      searchTerm,
      sortBy,
      categoryFilter,
      filteredSoldItems,
      formatPrice,
      selectedChartType,
      exportToExcel,
      exportToPDF,
      shareLink,
      showCopiedMessage,
      shareByWhatsApp,
      copyLink,
      migrationStatus,
      removeDuplicates,
      chartError,
      handleChartError,
      notification,
      formatDate,
      newSpecialItem,
      specialMenuItems,
      showSpecialMenuForm,
      isEditingSpecial,
      editingSpecialIndex,
      toggleSpecialMenuForm,
      onSpecialImageChange,
      saveSpecialMenuItem,
      clearSpecialMenuForm,
      editSpecialMenuItem,
      deleteSpecialMenuItem,
      sellSpecialMenuItem,
      toast,
      showToast,
      checkAvailabilityToast,
      isEmployee
    };
  }
};
