/**
 * Formatea un mensaje para WhatsApp asegurándose de que sea compatible con todos los dispositivos
 * @param {Object} data - Los datos para el mensaje
 * @returns {String} - El mensaje formateado
 */
export function formatOrderMessage(data) {
  const { customer, businessName, orderItems, total, notes } = data;
  
  // Usar caracteres especiales en lugar de emojis para decorar el mensaje
  let message = "★★★ NUEVO PEDIDO ★★★\n\n";
  
  // Información del cliente
  message += `➤ *Nombre${customer.email ? '/Email' : ''}:* ${customer.name}${customer.email ? ` / ${customer.email}` : ''}\n`;
  message += `➤ *Teléfono:* ${customer.phone}\n`;
  message += `➤ *Dirección:* ${customer.address}\n\n`;
  
  // Información del negocio
  message += `✧ *PEDIDO DE:* ${businessName || 'Restaurante'} ✧\n`;
  message += `----------------------------------\n`;
  
  // Detalles del pedido
  orderItems.forEach(item => {
    message += `◆ ${item.name} x${item.quantity}: $${(item.price * item.quantity).toFixed(2)}\n`;
  });
  
  message += `----------------------------------\n`;
  // Total
  message += `\n✓ *Total:* $${total.toFixed(2)}\n`;
  
  // Notas adicionales
  if (notes) {
    message += `\n✎ *Notas:* ${notes}\n`;
  }
  
  return message;
}

/**
 * Formatea un mensaje para compartir un enlace en WhatsApp
 * @param {String} link - El enlace a compartir
 * @param {String} businessName - Nombre del negocio (opcional)
 * @returns {String} - El mensaje formateado
 */
export function formatShareLinkMessage(link, businessName = '') {
  let message = `✨ ¡Hola! ${businessName ? `Te comparto el menú de ${businessName}` : 'Te comparto nuestro menú'} ✨\n\n`;
  message += `➥ Puedes verlo aquí: ${link}`;
  return message;
}
