// Marcar un plato como problem√°tico para evitar intentos continuos
const markPlatoAsSyncProblematic = async (platoId) => {
  try {
    // Actualizar el estado del plato en IndexedDB
    const db = await openDB();
    const transaction = db.transaction([PLATOS_STORE], 'readwrite');
    const store = transaction.objectStore(PLATOS_STORE);
    
    // Obtener el plato
    const getRequest = store.get(platoId);
    
    return new Promise((resolve, reject) => {
      getRequest.onsuccess = (event) => {
        const plato = event.target.result;
        
        if (!plato) {
          reject(new Error(`Plato ID ${platoId} no encontrado`));
          return;
        }
        
        // Marcar como problem√°tico y cambiar estado de sincronizaci√≥n
        plato.sync_problematic = true;
        plato.syncStatus = 'sync_error';
        plato.lastSyncAttempt = new Date().toISOString();
        
        // Guardar los cambios
        const updateRequest = store.put(plato);
        
        updateRequest.onsuccess = () => {
          console.log(`‚úÖ Plato ID ${platoId} marcado como problem√°tico para sincronizaci√≥n`);
          resolve(true);
        };
        
        updateRequest.onerror = (event) => {
          reject(new Error(`Error al actualizar plato: ${event.target.error}`));
        };
      };
      
      getRequest.onerror = (event) => {
        reject(new Error(`Error al obtener plato: ${event.target.error}`));
      };
    });
  } catch (error) {
    console.error('‚ùå Error al marcar plato como problem√°tico:', error);
    throw error;
  }
};

// M√©todo alternativo de sincronizaci√≥n con opciones m√°s resilientes
const syncPlatoUsingAlternativeMethod = async (preparedPlato, platoId, retryCount = 0) => {
  const maxRetries = 3;
  
  try {
    console.log('üì° Usando m√©todo alternativo de sincronizaci√≥n...');
    
    // Usar XMLHttpRequest para mayor control sobre la conexi√≥n
    return new Promise((resolve, reject) => {
      const xhr = new XMLHttpRequest();
      
      // Configurar timeout largo
      xhr.timeout = 60000; // 60 segundos
      
      xhr.onreadystatechange = async function() {
        if (xhr.readyState === 4) {
          if (xhr.status >= 200 && xhr.status < 300) {
            console.log('‚úÖ Sincronizaci√≥n exitosa usando m√©todo alternativo');
            
            try {
              // Actualizar estado en la base de datos
              await updatePlatoSyncStatus(platoId, 'synced');
            } catch (dbError) {
              console.error('‚ö†Ô∏è Error al actualizar estado en BD:', dbError);
            }
            
            resolve(true);
          } else {
            console.error(`‚ùå Error en sincronizaci√≥n alternativa: ${xhr.status}`);
            
            // Si hay error 500 y a√∫n tenemos reintentos
            if (xhr.status === 500 && retryCount < maxRetries) {
              console.warn(`‚ö†Ô∏è Reintentando (${retryCount+1}/${maxRetries})...`);
              
              // Espera exponencial entre reintentos
              setTimeout(async () => {
                try {
                  const result = await syncPlatoUsingAlternativeMethod(
                    preparedPlato, platoId, retryCount + 1
                  );
                  resolve(result);
                } catch (retryError) {
                  reject(retryError);
                }
              }, 5000 * Math.pow(2, retryCount));
            } else {
              reject(new Error(`Error HTTP: ${xhr.status}`));
            }
          }
        }
      };
      
      xhr.ontimeout = function() {
        console.error('‚ùå Timeout en la solicitud alternativa');
        reject(new Error('Timeout en la solicitud'));
      };
      
      xhr.onerror = function() {
        console.error('‚ùå Error de red en solicitud alternativa');
        
        // Reintento con backoff si todav√≠a hay intentos disponibles
        if (retryCount < maxRetries) {
          console.warn(`‚ö†Ô∏è Error de red, reintentando (${retryCount+1}/${maxRetries})...`);
          
          setTimeout(async () => {
            try {
              const result = await syncPlatoUsingAlternativeMethod(
                preparedPlato, platoId, retryCount + 1
              );
              resolve(result);
            } catch (retryError) {
              reject(retryError);
            }
          }, 5000 * Math.pow(2, retryCount));
        } else {
          reject(new Error('Error de red persistente'));
        }
      };
      
      // Abrir conexi√≥n con m√©todo POST
      xhr.open('POST', `${API_URL}/platos`, true);
      
      // Establecer headers
      xhr.setRequestHeader('Content-Type', 'application/json');
      xhr.setRequestHeader('X-Requested-With', 'XMLHttpRequest');
      
      // Enviar los datos
      xhr.send(JSON.stringify(preparedPlato));
    });
  } catch (error) {
    console.error('‚ùå Error en m√©todo alternativo de sincronizaci√≥n:', error);
    throw error;
  }
};

// Sincronizar un plato con el servidor
const syncPlatoToServer = async (plato, retryCount = 0) => {
  const maxRetries = SYNC_CONFIG.maxRetries;
  
  // Si el plato est√° marcado como problem√°tico, omitirlo
  if (plato.sync_problematic) {
    console.warn(`‚ö†Ô∏è Omitiendo plato ${plato.id} marcado como problem√°tico para sincronizaci√≥n`);
    return false;
  }
  
  console.log(`Sincronizando plato ${plato.id} con el servidor (intento ${retryCount + 1}/${maxRetries + 1})...`);
  
  try {
    // Omitir verificaci√≥n si est√° desactivada
    if (!SYNC_CONFIG.skipServerAvailabilityCheck) {
      // Verificar si el servidor est√° disponible
      const serverAvailable = await checkServerAvailability();
      if (!serverAvailable) {
        console.error('‚ùå El servidor no est√° disponible. Posponiendo sincronizaci√≥n.');
        return false;
      }
    }
    
    // Preparar datos ultra-minimalistas para sincronizaci√≥n
    let syncData;
    
    try {
      // Intentar usar la funci√≥n de preparaci√≥n normal
      const preparedPlato = preparePlatoForSync(plato);
      
      // Verificar tama√±o y aplicar l√≠mites estrictos
      const dataSize = JSON.stringify(preparedPlato).length;
      if (dataSize > SYNC_CONFIG.maxPlatoSize) {
        console.warn(`‚ö†Ô∏è Plato demasiado grande (${dataSize} bytes). Usando versi√≥n ultra-minimalista.`);
        syncData = createUltraMinimalPlato(plato);
      } else {
        syncData = preparedPlato;
      }
    } catch (prepError) {
      console.error('‚ùå Error al preparar datos:', prepError);
      syncData = createUltraMinimalPlato(plato);
    }
    
    // Usar el m√©todo m√°s confiable para sincronizaci√≥n
    return await syncMinimalDataOnly(syncData, plato.id, retryCount);
  } catch (error) {
    console.error('‚ùå Error en sincronizaci√≥n:', error);
    
    if (retryCount >= maxRetries) {
      try {
        await markPlatoAsSyncProblematic(plato.id);
      } catch (markError) {
        console.error('Error al marcar plato como problem√°tico:', markError);
      }
    }
    
    return false;
  }
};

// Versi√≥n ultra-minimalista para casos extremos
const createUltraMinimalPlato = (plato) => {
  return {
    id: plato.id,
    name: plato.name?.substring(0, 30) || 'Plato sin nombre',
