import "core-js/modules/es.array.push.js";
import "core-js/modules/es.iterator.constructor.js";
import "core-js/modules/es.iterator.find.js";
import { ref, onMounted, computed } from 'vue';
import { getBusinessInfo, saveBusinessInfo, getMenuItems, saveMenuItem, deleteMenuItem, updateItemAvailability } from '../services/storageService';
export default {
  name: 'MenuComponent',
  setup() {
    // Estado para información del negocio
    const businessInfo = ref({
      id: 'business_info_1',
      name: '',
      description: '',
      address: '',
      contact: '',
      logo: ''
    });
    const showBusinessForm = ref(false);

    // Estado para información de pago
    const paymentInfo = ref({
      qrTitle: '',
      qrImage: '',
      nequiNumber: '',
      nequiImage: ''
    });
    const showPaymentForm = ref(false);

    // Estado para menú del restaurante
    const menuItems = ref([]);
    const showMenuForm = ref(false);
    const isEditing = ref(false);
    const newItem = ref({
      id: '',
      name: '',
      description: '',
      price: 0,
      includesDrink: false,
      availableQuantity: 0,
      image: ''
    });

    // Estado para notificaciones
    const notification = ref({
      show: false,
      message: '',
      type: 'success',
      timeout: null
    });

    // Estado para indicadores de carga
    const loading = ref({
      businessInfo: false,
      paymentInfo: false,
      menu: false
    });

    // Estado para mensaje de migración de datos
    const migrationStatus = ref({
      inProgress: false,
      completed: false,
      message: ''
    });

    // Estado para toast notification
    const toast = ref({
      show: false,
      message: '',
      type: 'success'
    });

    // Cargar datos al montar el componente
    onMounted(async () => {
      try {
        // Cargar información del negocio desde IndexedDB
        const storedBusinessInfo = await getBusinessInfo();
        if (storedBusinessInfo) {
          businessInfo.value = storedBusinessInfo;
        }

        // Cargar menú del restaurante
        const items = await getMenuItems();
        if (items && items.length > 0) {
          menuItems.value = items;
        }
        console.log('Datos cargados correctamente');
      } catch (error) {
        console.error('Error al cargar datos:', error);
        notification.value = {
          show: true,
          message: 'Error al cargar datos. Por favor, intente nuevamente.',
          type: 'error',
          timeout: setTimeout(() => {
            notification.value.show = false;
          }, 3000)
        };
      }
    });

    // Métodos para información del negocio
    const toggleBusinessForm = () => {
      showBusinessForm.value = !showBusinessForm.value;
    };
    const onLogoChange = event => {
      const file = event.target.files[0];
      if (file) {
        const reader = new FileReader();
        reader.onload = e => {
          businessInfo.value.logo = e.target.result;
        };
        reader.readAsDataURL(file);
      }
    };
    const saveBusinessInfo = async () => {
      try {
        if (notification.value.timeout) {
          clearTimeout(notification.value.timeout);
        }

        // Mostrar indicador de carga
        loading.value.businessInfo = true;
        await saveBusinessInfo(businessInfo.value);

        // Ocultar indicador de carga
        loading.value.businessInfo = false;
        notification.value = {
          show: true,
          message: 'Información del negocio guardada con éxito!',
          type: 'success',
          timeout: setTimeout(() => {
            notification.value.show = false;
          }, 3000)
        };
      } catch (error) {
        // Ocultar indicador de carga en caso de error
        loading.value.businessInfo = false;
        console.error('Error al guardar información del negocio:', error);
        notification.value = {
          show: true,
          message: 'Error al guardar información del negocio',
          type: 'error',
          timeout: setTimeout(() => {
            notification.value.show = false;
          }, 3000)
        };
      }
    };
    const clearBusinessForm = () => {
      businessInfo.value = {
        id: 'business_info_1',
        name: '',
        description: '',
        address: '',
        contact: '',
        logo: ''
      };
    };

    // Métodos para información de pago
    const togglePaymentForm = () => {
      showPaymentForm.value = !showPaymentForm.value;
    };
    const onQRImageChange = event => {
      const file = event.target.files[0];
      if (file) {
        const reader = new FileReader();
        reader.onload = e => {
          paymentInfo.value.qrImage = e.target.result;
        };
        reader.readAsDataURL(file);
      }
    };
    const onNequiImageChange = event => {
      const file = event.target.files[0];
      if (file) {
        const reader = new FileReader();
        reader.onload = e => {
          paymentInfo.value.nequiImage = e.target.result;
        };
        reader.readAsDataURL(file);
      }
    };
    const savePaymentInfo = async () => {
      try {
        if (notification.value.timeout) {
          clearTimeout(notification.value.timeout);
        }

        // Mostrar indicador de carga
        loading.value.paymentInfo = true;
        await saveBusinessInfo({
          ...businessInfo.value,
          paymentInfo: paymentInfo.value
        });

        // Ocultar indicador de carga
        loading.value.paymentInfo = false;
        notification.value = {
          show: true,
          message: 'Información de pago guardada con éxito!',
          type: 'success',
          timeout: setTimeout(() => {
            notification.value.show = false;
          }, 3000)
        };
      } catch (error) {
        // Ocultar indicador de carga en caso de error
        loading.value.paymentInfo = false;
        console.error('Error al guardar información de pago:', error);
        notification.value = {
          show: true,
          message: 'Error al guardar información de pago',
          type: 'error',
          timeout: setTimeout(() => {
            notification.value.show = false;
          }, 3000)
        };
      }
    };
    const clearPaymentForm = () => {
      paymentInfo.value = {
        qrTitle: '',
        qrImage: '',
        nequiNumber: '',
        nequiImage: ''
      };
    };

    // Métodos para menú del restaurante
    const toggleMenuForm = () => {
      showMenuForm.value = !showMenuForm.value;
    };
    const onImageChange = event => {
      const file = event.target.files[0];
      if (file) {
        const reader = new FileReader();
        reader.onload = e => {
          newItem.value.image = e.target.result;
        };
        reader.readAsDataURL(file);
      }
    };
    const saveMenuItem = async () => {
      try {
        if (notification.value.timeout) {
          clearTimeout(notification.value.timeout);
        }

        // Mostrar indicador de carga
        loading.value.menu = true;

        // Validar campos obligatorios
        if (!newItem.value.name || newItem.value.name.trim() === '') {
          loading.value.menu = false;
          notification.value = {
            show: true,
            message: 'El nombre del plato es obligatorio',
            type: 'error',
            timeout: setTimeout(() => {
              notification.value.show = false;
            }, 3000)
          };
          return;
        }
        if (!newItem.value.price || isNaN(newItem.value.price) || newItem.value.price <= 0) {
          loading.value.menu = false;
          notification.value = {
            show: true,
            message: 'El precio debe ser un número mayor que cero',
            type: 'error',
            timeout: setTimeout(() => {
              notification.value.show = false;
            }, 3000)
          };
          return;
        }

        // Actualizar la lista local
        if (isEditing.value) {
          const index = menuItems.value.findIndex(item => item.id === newItem.value.id);
          if (index !== -1) {
            menuItems.value[index] = {
              ...newItem.value
            };
          }
        } else {
          menuItems.value.push({
            ...newItem.value
          });
        }

        // Guardar en IndexedDB
        await saveMenuItem(newItem.value);

        // Ocultar indicador de carga
        loading.value.menu = false;
        notification.value = {
          show: true,
          message: 'Plato guardado con éxito!',
          type: 'success',
          timeout: setTimeout(() => {
            notification.value.show = false;
          }, 3000)
        };

        // Limpiar el formulario y cerrar el modal
        clearMenuForm();
        showMenuForm.value = false;
      } catch (error) {
        // Ocultar indicador de carga en caso de error
        loading.value.menu = false;
        console.error('Error al guardar plato:', error);
        notification.value = {
          show: true,
          message: 'Error al guardar el plato',
          type: 'error',
          timeout: setTimeout(() => {
            notification.value.show = false;
          }, 3000)
        };
      }
    };
    const editMenuItem = index => {
      const item = menuItems.value[index];
      newItem.value = {
        ...item
      };
      isEditing.value = true;
      showMenuForm.value = true;
    };
    const sellMenuItem = async index => {
      try {
        const item = menuItems.value[index];

        // Actualizar la cantidad disponible
        item.availableQuantity -= 1;

        // Guardar en IndexedDB
        await saveMenuItem(item);
        notification.value = {
          show: true,
          message: 'Plato vendido con éxito!',
          type: 'success',
          timeout: setTimeout(() => {
            notification.value.show = false;
          }, 3000)
        };
      } catch (error) {
        console.error('Error al vender plato:', error);
        notification.value = {
          show: true,
          message: 'Error al vender el plato',
          type: 'error',
          timeout: setTimeout(() => {
            notification.value.show = false;
          }, 3000)
        };
      }
    };
    const deleteMenuItem = async index => {
      try {
        const item = menuItems.value[index];

        // Eliminar de la lista local
        menuItems.value.splice(index, 1);

        // Eliminar de IndexedDB
        await deleteMenuItem(item.id);
        notification.value = {
          show: true,
          message: 'Plato eliminado con éxito!',
          type: 'success',
          timeout: setTimeout(() => {
            notification.value.show = false;
          }, 3000)
        };
      } catch (error) {
        console.error('Error al eliminar plato:', error);
        notification.value = {
          show: true,
          message: 'Error al eliminar el plato',
          type: 'error',
          timeout: setTimeout(() => {
            notification.value.show = false;
          }, 3000)
        };
      }
    };
    const clearMenuForm = () => {
      newItem.value = {
        id: '',
        name: '',
        description: '',
        price: 0,
        includesDrink: false,
        availableQuantity: 0,
        image: ''
      };
      isEditing.value = false;
    };
    const checkAvailabilityToast = itemId => {
      const item = menuItems.value.find(item => item.id === itemId);
      if (item && item.availableQuantity <= 0) {
        toast.value = {
          show: true,
          message: 'Este plato no está disponible',
          type: 'error'
        };
      }
    };
    return {
      businessInfo,
      showBusinessForm,
      toggleBusinessForm,
      onLogoChange,
      saveBusinessInfo,
      clearBusinessForm,
      paymentInfo,
      showPaymentForm,
      togglePaymentForm,
      onQRImageChange,
      onNequiImageChange,
      savePaymentInfo,
      clearPaymentForm,
      menuItems,
      showMenuForm,
      toggleMenuForm,
      onImageChange,
      saveMenuItem,
      editMenuItem,
      sellMenuItem,
      deleteMenuItem,
      clearMenuForm,
      notification,
      loading,
      migrationStatus,
      toast,
      checkAvailabilityToast
    };
  }
};
