import { onMounted, onUnmounted, ref } from 'vue';
import { saveWhatsAppReservation } from '../services/reservaService';
import eventBus from '../utils/eventBus';
export default {
  __name: 'WhatsAppReservationHandler',
  setup(__props, {
    expose: __expose
  }) {
    const isListening = ref(false);
    const lastProcessedMessageId = ref(null);

    // Funci√≥n para procesar mensajes de WhatsApp
    const processWhatsAppMessage = async event => {
      // Solo procesamos mensajes que vengan de la ventana principal o del mismo origen
      if (event.source !== window && event.source !== window.parent) return;
      try {
        // Verificar que el mensaje sea de tipo reserva de WhatsApp
        if (event.data && event.data.type === 'whatsapp_reservation') {
          console.log('üì± Recibido mensaje de reserva desde WhatsApp:', event.data);

          // Evitar procesar el mismo mensaje dos veces
          if (lastProcessedMessageId.value === JSON.stringify(event.data)) {
            console.log('‚ö†Ô∏è Mensaje ya procesado, ignorando duplicado');
            return;
          }
          lastProcessedMessageId.value = JSON.stringify(event.data);

          // Guardar la reserva usando el servicio
          const result = await saveWhatsAppReservation(event.data.reservation);
          if (result.success) {
            console.log('‚úÖ Reserva de WhatsApp guardada correctamente:', result.reservation);

            // Notificar a la ventana que envi√≥ el mensaje
            if (event.source && event.source.postMessage) {
              event.source.postMessage({
                type: 'whatsapp_reservation_response',
                success: true,
                message: 'Reserva recibida correctamente'
              }, '*');
            }
          } else {
            throw new Error(result.error);
          }
        }
      } catch (error) {
        console.error('‚ùå Error al procesar mensaje de WhatsApp:', error);

        // Notificar error a la ventana que envi√≥ el mensaje
        if (event.source && event.source.postMessage) {
          event.source.postMessage({
            type: 'whatsapp_reservation_response',
            success: false,
            message: 'Error al procesar la reserva: ' + error.message
          }, '*');
        }
      }
    };

    // Funci√≥n para procesar mensajes del eventBus
    const processEventBusMessage = async message => {
      try {
        console.log('üì± Recibido mensaje de reserva desde eventBus:', message);

        // Verificar que el mensaje sea de tipo reserva de WhatsApp
        if (message && message.type === 'whatsapp_reservation') {
          // Evitar procesar el mismo mensaje dos veces
          if (lastProcessedMessageId.value === JSON.stringify(message)) {
            console.log('‚ö†Ô∏è Mensaje ya procesado, ignorando duplicado');
            return;
          }
          lastProcessedMessageId.value = JSON.stringify(message);

          // Guardar la reserva usando el servicio
          const result = await saveWhatsAppReservation(message.reservation);
          if (result.success) {
            console.log('‚úÖ Reserva de WhatsApp (eventBus) guardada correctamente:', result.reservation);
          } else {
            throw new Error(result.error);
          }
        }
      } catch (error) {
        console.error('‚ùå Error al procesar mensaje de WhatsApp desde eventBus:', error);
      }
    };

    // Configurar el listener de mensajes al montar el componente
    onMounted(() => {
      if (!isListening.value) {
        // Escuchar mensajes de window
        window.addEventListener('message', processWhatsAppMessage);

        // Escuchar mensajes del eventBus
        eventBus.on('whatsapp_message', processEventBusMessage);
        isListening.value = true;
        console.log('üîÑ WhatsAppReservationHandler montado y escuchando mensajes');

        // Exponer la funci√≥n de simulaci√≥n en window para pruebas
        window.testWhatsAppReservation = simulateWhatsAppReservation;
      }
    });

    // Limpiar el listener al desmontar el componente
    onUnmounted(() => {
      if (isListening.value) {
        // Eliminar listener de window
        window.removeEventListener('message', processWhatsAppMessage);

        // Eliminar listener del eventBus
        eventBus.off('whatsapp_message', processEventBusMessage);
        isListening.value = false;
        console.log('üõë WhatsAppReservationHandler desmontado');

        // Eliminar la funci√≥n de simulaci√≥n
        if (window.testWhatsAppReservation) {
          delete window.testWhatsAppReservation;
        }
      }
    });

    // Funci√≥n para simular una reserva de WhatsApp (para pruebas)
    const simulateWhatsAppReservation = async (customData = {}) => {
      try {
        console.log(' Simulando reserva de WhatsApp con datos:', customData);

        // Datos por defecto para la reserva
        const defaultData = {
          nombre: 'Cliente de Prueba WhatsApp',
          telefono: '612345678',
          email: 'whatsapp@example.com',
          fecha: new Date().toISOString().split('T')[0],
          // Hoy
          hora: '20:00',
          personas: 3,
          notas: 'Reserva de prueba desde WhatsApp'
        };

        // Combinar datos por defecto con datos personalizados
        const whatsappData = {
          ...defaultData,
          ...customData
        };

        // Crear un evento similar al que enviar√≠a WhatsApp
        const whatsappEvent = {
          data: {
            type: 'whatsapp_reservation',
            reservation: whatsappData,
            timestamp: Date.now()
          },
          source: window
        };

        // Procesar el evento simulado
        await processWhatsAppMessage(whatsappEvent);
        return true;
      } catch (error) {
        console.error(' Error al simular reserva de WhatsApp:', error);
        return false;
      }
    };

    // Exponer la funci√≥n de simulaci√≥n para pruebas
    __expose({
      simulateWhatsAppReservation
    });
    const __returned__ = {
      isListening,
      lastProcessedMessageId,
      processWhatsAppMessage,
      processEventBusMessage,
      simulateWhatsAppReservation,
      onMounted,
      onUnmounted,
      ref,
      get saveWhatsAppReservation() {
        return saveWhatsAppReservation;
      },
      get eventBus() {
        return eventBus;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
